    IERC20(dest).safeTransfer(msg.sender, toUserAmount);
    // get the fee
    _payFee(feeToken, fee);

    emit LogTokenSwap(
      msg.sender,
      src,
      srcAmount,
      dest,
      toUserAmount
    );
  }

