// File: contracts/payment/BuyItemV2.sol

pragma solidity 0.5.15;




contract BuyItemV2 is ReentrancyGuard, Ownable {
  using SafeMath for uint256;

  event EventBuyItem(address _to, uint256 _value);
  event EventWithdraw(address _to, uint256 _value);


  function () external payable {
    require (address(msg.sender) != address(0) && address(msg.sender) != address(this), 'wrong address');
    emit EventBuyItem(msg.sender, msg.value);
  }


  // withdraw
  function withdraw (uint256 _amount, address payable _account)
    public onlyOwner {
    require (
      address(_account) != address(0) &&
      address(_account) != address(this), 'wrong address');
    require(_amount <= address(this).balance, 'wrong price');

    _account.transfer(_amount);
    emit EventWithdraw(_account, _amount);
  }
}
