pragma solidity 0.6.8;
// SPDX-License-Identifier: UNLICENSED


contract Test {
    event Swap (uint[] amounts);
    IUniswapV2Router01 public uniswapRouter = IUniswapV2Router01(0xf164fC0Ec4E93095b804a4795bBe1e041497b92a);
    
    function swap(uint currentPrice, uint maxSpread) public payable {
        address[] memory path = new address[](2);
        path[0] = uniswapRouter.WETH();
        path[1] = 0x6B175474E89094C44Da98b954EedeAC495271d0F;
        uint[] memory amounts = uniswapRouter.swapExactETHForTokens {
          value: msg.value
        }(
          msg.value * currentPrice * maxSpread / 1e10,
          path,
          msg.sender,
          now
        );
        
        emit Swap(amounts);
    }
    
}



