// File: contracts/Notary.sol

pragma solidity 0.6.6;

pragma experimental ABIEncoderV2;



contract Notary is Ownable {
    using SafeMath for uint256;

    struct Account {
        bool isWhitelisted;
        uint256 notarizationsLimit;
        uint256 notarizationFee;
    }
    mapping(address => Account) public accounts;

    mapping(bytes32 => bool) public notarizedFiles;

    event AccountWhitelisted(address account);
    event AccountBlacklisted(address account);
    event FileNotarized(
        address indexed principal,
        bytes32 indexed hash,
        string name
    );

    function blacklistAccount(address account) external onlyOwner {
        require(accounts[account].isWhitelisted, "this account is already blacklisted");

        delete accounts[account];
        emit AccountBlacklisted(account);
    }

    function notarizeFile(bytes32 hash, string calldata name) external payable {
        require(!notarizedFiles[hash], "this file was already notarized");
        bytes memory _name = bytes(name);
        require(_name.length > 0, "file name cannot be empty");

        Account storage account = accounts[msg.sender];

        require(account.isWhitelisted, "you are not allowed to notarize files");
        require(
            account.notarizationsLimit > 0,
            "notarizations limit exceeded"
        );
        require(msg.value >= account.notarizationFee, "insufficient notarization fee");

        notarizedFiles[hash] = true;
        emit FileNotarized(msg.sender, hash, name);

        account.notarizationsLimit = account.notarizationsLimit.sub(
            1
        );
    }

    function setNotarizationFee(address account, uint256 notarizationFee) external onlyOwner {
        accounts[account].notarizationFee = notarizationFee;
    }

    function setNotarizationsLimit(address account, uint256 notarizationsLimit)
        external
        onlyOwner
    {
        accounts[account].notarizationsLimit = notarizationsLimit;
    }

    function whitelistAccount(address _address) external onlyOwner {
        Account storage account = accounts[_address];
        require(!account.isWhitelisted, "this account is already whitelisted");

        account.isWhitelisted = true;
        if (account.notarizationsLimit == 0 && account.notarizationFee == 0) {
            account.notarizationsLimit = 1;
        }
        emit AccountWhitelisted(_address);
    }

    function withdrawNotarizationFees() external onlyOwner {
        address payable _owner = payable(owner());
        uint256 balance = address(this).balance;
        _owner.transfer(balance);
    }

    function updateAccount(address _address, Account memory account) public onlyOwner {
        accounts[_address] = account;
    }
}
