/**
 * @title SignedAirdrop
 * @author cbruguera
 * @dev Airdrop contract using SignatureBouncer permissioning. Allows senders to claim a reward if
 * they provide a valid signature.
 */
contract SignedAirdrop is SignatureBouncer {
    using SafeERC20 for IERC20;
    using ECDSA for bytes32;

    IERC20 public token;
    mapping(bytes32 => bool) public claimed;

    event RewardGranted(address sender, uint256 amount);

    modifier onlyAdmin() {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "sender is not admin");
        _;
    }

    constructor(address _token)
        public
    {
        token = IERC20(_token);
    }

    /**
    * @dev is the signature of `this + sender` from a bouncer?
    * @return bool
    */
    function isValidSignature(address _address, uint256 amount, bytes memory _sig, uint256 nonce)
        internal
        view
        returns (bool)
    {
        return isValidDataHash(
            keccak256(abi.encodePacked(address(this), amount, _address, nonce)),
            _sig
        );
    }

    /**
    * @dev claims reward given a valid signature and corresponding nonce
    * @param amount - amount of tokens to withdraw (already hashed into the signature)
    * @param _sig - signature provided by an approved "signature bouncer" in the contract
    * @param nonce - random nonce provided by signer
    */
    function claimReward(uint256 amount, bytes memory _sig, uint256 nonce)
        public
    {
        bytes32 sighash = keccak256(_sig);
        require(claimed[sighash] == false, "reward was already claimed");
        require(isValidSignature(msg.sender, amount, _sig, nonce), "invalid signature provided");
        claimed[sighash] = true;
        token.safeTransfer(msg.sender, amount);
        emit RewardGranted(msg.sender, amount);
    }

    /**
    * @dev withdraws arbitrary amount of tokens, callable by admin only
    * @param amount - amount of tokens to withdraw
    */
    function withdraw(uint256 amount)
        public
        onlyAdmin
    {
        token.safeTransfer(msg.sender, amount);
    }
}
