pragma solidity 0.5.7;

contract Faucet is Ownable, ReentrancyGuard {

    bool public contractState;
    uint256 public tokenAmount;
    
    constructor() public {
        contractState = true;
        tokenAmount = 50000000000000000000;
    }
    
    modifier onlyActive() {
    require(contractState == true, "contract is not active");
    _;
    }

    function pauseContract() external onlyOwner onlyActive {
        contractState = false;
    }

    function unPauseContract() external onlyOwner {
        require(!contractState, "contract is already active");
        contractState = true;
    }
    
    function sendTokens(address _token) external nonReentrant onlyActive {
        require(
            IERC20(_token).transfer(msg.sender, tokenAmount),
            "transfer failed in transfer of rewards"
        );
    }
    
    function setTokenAmount(uint256 _value) external onlyOwner {
        tokenAmount = _value;
    }
    
}
