contract CryptoChallengeN is usingProvable, Pausable, Configurable {

    using SafeMath for uint256;
    using strings for *;
	uint256 constant PRECISION = 1000;

    /// @dev events
    event NewRace(uint256 race_id, uint256 race_end, bytes32 message, address indexed creator);
    event RaceEnded(uint256 race_id);
    event RaceRefunded(uint256 race_id, string reason);
    event BetClaimed(uint256 race_id, address indexed player, uint256 amount);
    event BetPlaced(uint256 race_id, address indexed bettor);
    event ConfigChanged(bytes32 name, uint256 new_value);
    event CoinStatusChanged(bytes32 symbol, bool is_whitelisted);
    event TimestampsValidationFailed();

    /// @dev possible states of a race
    enum State {READY, WAITING_QUERY1, WAITING_QUERY2, DONE, REFUND}

    /// @dev provable queries
    string constant public query_string_part1 = "[URL] json(https://min-api.cryptocompare.com/data/pricemultifull?fsyms=";
    string constant public query_string_part2 = "&tsyms=USD&sign=true).[RAW].[";
    string constant public query_string_part3 = "].[USD].[PRICE,LASTUPDATE]";
    /// @dev current roll id
    uint256 public current_race = 1;
    /// @dev total amount of collected eth by the house
    uint256 public house;

    /// @dev describes a user bet
    struct Bet {
        //amount of wagered ETH in Wei
        mapping(bytes32 => uint256) amount_coin;
    }

    /// @dev describes a roll
    struct RaceConfig {
        // provable query ids
        bytes32 query_price_start;
		bytes32 query_price_end;

        bytes32[] coins;
        uint256[] coin_pool;
        uint256 total_pool;

        uint256 race_duration;
		uint256 min_bet;
		uint256 start_time;
		uint256 betting_duration;
		uint256 bet_matching_pool;
        uint256 paid; //amount paid to claimers

		address creator;

        State state;
    }

	struct RaceResult {
		uint256 winners_total;
		// query results
        mapping(bytes32 => uint32) result_price_start;
        mapping(bytes32 => uint32) result_price_end;
        mapping(bytes32 => bool) coin_won;
	}

    mapping(uint256 => RaceConfig) public races;
	mapping(uint256 => RaceResult) public races_result;
    mapping(uint256 => mapping(address => bool)) public claimed;
	mapping(uint256 => mapping(address => Bet)) internal _bets;
    /// @dev only allowed coins can be listed in a race
    mapping(bytes32 => bool) public whitelisted_coin;
    mapping(bytes32 => uint256) internal _query_to_race;
    /// @dev mapping to prevent processing twice the same query
    mapping(bytes32 => bool) internal _processed;

    /**
        @dev init the contract
    */
    constructor() public
    Pausable()
    Configurable() {
		//TLSNotary proof for URLs
        whitelisted_coin["BTC"] = true;
        whitelisted_coin["LTC"] = true;
        whitelisted_coin["ETH"] = true;
        whitelisted_coin["XRP"] = true;
        whitelisted_coin["BNB"] = true;
        whitelisted_coin["DAI"] = true;

        config["REFUND_DELAY"] = 12 hours;		    /// time before a started roll has to reach the DONE state before it can be refunded
        config["MIN_DURATION"] = 10 minutes;		/// minimal allowed duration
        config["MAX_DURATION"] = 7 days;		    /// maximal allowed duration
        config["MIN_BET"] = 1;		                /// minimal allowed bet
        config["MAX_BET"] = 100 ether;		        /// maximal allowed bet
        config["TIMESTAMP_TOLERANCE"] = 150;        /// price timestamp tolerance in seconds
        config["GAS_LIMIT_ONE"] = 350000; 		    /// gas limit for first price callbacks
        config["GAS_LIMIT_TWO"] = 450000; 		    /// gas limit for second price callbacks
        config["HOUSE_EDGE"] = 75;                  /// house edge
        config["MIN_BET_DURATION"] = 5 minutes;     /// minimal betting duration

        provable_setProof(proofType_TLSNotary | proofStorage_IPFS);
    }

    /**
        @dev allows anyone to start a new race
        @param race_duration Duration of the racing phase
        @param betting_duration Duration of the betting phase
        @param min_bet Minimal allowed bet
        @param bet_matching_pool Amount of Eth sent by the creator to be used as betting pool
        @param start_msg Message to associate with the race (sent in the NewRace event)
        @param coins Coins participating in the rase from the whitelisted ones
        @param custom_gas_price Set to 0 for default value
    */
    function newRace(
    uint256 race_duration,
    uint256 betting_duration,
    uint256 min_bet,
    uint256 bet_matching_pool,
    bytes32 start_msg,
    bytes32[] memory coins,
    uint256 custom_gas_price
    ) public payable
    whenNotPaused() {
        provable_setCustomGasPrice(custom_gas_price);

		require(race_duration >= config["MIN_DURATION"] && race_duration <= config["MAX_DURATION"],"Invalid duration");
		require(min_bet > config["MIN_BET"], "Must set a greater minimal bet");
		require(betting_duration >= config["MIN_BET_DURATION"], "Betting duration is too short");
        require(coins.length > 1, "At least 2 coins!");
        require(coins.length <= 10, "Max 10 coins");

		emit NewRace(current_race, now + betting_duration + race_duration, start_msg, msg.sender);

        //compute provable fees
        uint256 call_price = provable_getPrice("URL", config["GAS_LIMIT_ONE"]);
        call_price += provable_getPrice("URL", config["GAS_LIMIT_TWO"]);
        require(call_price.add(bet_matching_pool) <= msg.value, "Not enough funds");
		//lets race!
		RaceConfig storage race = races[current_race];
		uint256 previous_balance = address(this).balance;

        race.coins = new bytes32[](coins.length);
        race.coin_pool = new uint256[](coins.length);
        //collect the coins
        for(uint i = 0; i < coins.length; i++) {
            require(whitelisted_coin[coins[i]] == true, "Unauthorized coin symbol");
            race.coins[i] = coins[i];
        }

		uint256 starts_in = now.add(betting_duration).sub(block.timestamp);
		uint256 ends_in = now.add(betting_duration).sub(block.timestamp).add(race_duration);
        string memory coins_string = _buildCoinsString(race.coins);

        string memory query_string = query_string_part1.toSlice().concat(coins_string.toSlice()).toSlice().concat(query_string_part2.toSlice()).toSlice().concat(coins_string.toSlice()).toSlice().concat(query_string_part3.toSlice());

        //save the provable query indices for proof
		race.query_price_start = provable_query(starts_in, "nested", query_string, config["GAS_LIMIT_ONE"]);
		race.query_price_end = provable_query(ends_in, "nested", query_string, config["GAS_LIMIT_TWO"]);

		race.state = State.WAITING_QUERY1;
		race.race_duration = race_duration;
		race.min_bet = min_bet;
		race.betting_duration = betting_duration;
		race.bet_matching_pool = bet_matching_pool;
		race.creator = msg.sender;
        race.start_time = now;

		//save mappings to find the right race number associated with this query
		_query_to_race[race.query_price_start] = current_race;
		_query_to_race[race.query_price_end] = current_race;

        uint256 spent = previous_balance.sub(address(this).balance).add(bet_matching_pool);

		uint256 rest = msg.value.sub(spent);
		if(rest > 0) {
			msg.sender.transfer(rest);
		}
		_prepareNextRace();
	}

    /**
        @dev Allows to place a bet on a specific race
        @param race_id : race number
    */
    function placeBet(uint256 race_id, uint256[] calldata coin_bet) external payable
	whenNotPaused() {
        require(msg.value <= config["MAX_BET"], "bet is higher than the maximum allowed");
        RaceConfig storage race = races[race_id];
        require(coin_bet.length == race.coins.length,"Invalid bets count");
		uint256 total_bet = 0;
        uint256 untouched = 0; //amount of coins this bet doesnt bet on
        for(uint i = 0; i < coin_bet.length; i++) {
           total_bet = total_bet.add(coin_bet[i]);
           if(coin_bet[i] == 0) {
                untouched++;
           }
        }
		uint256 house_edge = total_bet.div(PRECISION).mul(config["HOUSE_EDGE"]);
		house = house.add(house_edge);

		uint256 betting_ends = race.start_time.add(race.betting_duration);

		require(race.state == State.WAITING_QUERY1, "Invalid race state");
		require(block.timestamp >= race.start_time, "Not in betting phase");
		require(block.timestamp <= betting_ends, "Not in betting phase");
		require(msg.value == total_bet.add(house_edge), "Invalid payment");
		require(total_bet >= race.min_bet, "Invalid bet");

		_placeBet(race_id, msg.sender, coin_bet);

		if(race.creator == msg.sender) {
            return; //do not match creators bet
        }

        if(untouched == 0) {
            return; //the user bet on all coins, we dont match
        }

		uint256 funds = race.bet_matching_pool >= total_bet? total_bet : race.bet_matching_pool;
		if(funds > 0) {

            uint256 reserve = funds;
            uint256 matchPerCoin = reserve / untouched;
            uint256[] memory match_bets = new uint256[](race.coins.length);

            for(uint i = 0; i < coin_bet.length; i++) {
                if(coin_bet[i] == 0) {
                    match_bets[i] = matchPerCoin;
                    reserve = reserve.sub(matchPerCoin);
                }
            }
            //matchPerCoin is a division so dust can remain, handle it
            if(reserve > 0) {
                house = house.add(reserve); //just give the dust to house
            }

			_placeBet(race_id, race.creator, match_bets);
			race.bet_matching_pool = race.bet_matching_pool.sub(funds);
		}
    }

    /**
        @dev Allows to claim the winnings from a race
        @param race_id Race number of the race to claim from
    */
    function claim(uint256 race_id) external {
        RaceConfig storage race = races[race_id];
        RaceResult storage race_result = races_result[race_id];
        Bet storage bet = _bets[race_id][msg.sender];
        require(!claimed[race_id][msg.sender], "Already claimed");

		uint256 total_bet = 0;
        for(uint i = 0; i < race.coins.length; i++) {
            total_bet = total_bet.add(bet.amount_coin[race.coins[i]]);
        }

        require((total_bet > 0) || (race.creator == msg.sender), "Not a bettor or a creator");

        //handle refunding if contract isnt in the DONE or REFUND state after the refund delay is passed
        //also prevent refunding not started rolls
        if(race.state != State.DONE && race.state != State.REFUND) {
            //allow refund after the delay has passed
			uint256 refund_time = race.start_time.add(race.betting_duration).add(config["REFUND_DELAY"]);
            if(block.timestamp >= refund_time) {
                race.state = State.REFUND;
                emit RaceRefunded(race_id, "Failed to get start or end prices callback transaction");
            }
        }

		require(race.state == State.REFUND || race.state == State.DONE,"Cant claim from this race");
 
        //handle payments from refunded rolls
        if(race.state == State.REFUND) {
			if(race.creator == msg.sender) {
				total_bet = total_bet.add(race.bet_matching_pool);
				race.bet_matching_pool = 0;
			}
            if(total_bet > 0) {
                claimed[race_id][msg.sender] = true;
				msg.sender.transfer(total_bet);
			}
        } else {
			uint256 wagered_winners_total = 0;

            for(uint i = 0; i < race.coins.length; i++) {
                bytes32 coin = race.coins[i];
                if(race_result.coin_won[coin] == true) {
                    wagered_winners_total = wagered_winners_total.add(bet.amount_coin[coin]);
                }
            }
			uint256 to_pay = wagered_winners_total.mul(PRECISION).div(race_result.winners_total).mul(race.total_pool).div(PRECISION);
            // check if this race can pay due to dust errors
            if(race.total_pool.sub(race.paid) < to_pay) {
                // it cant, adjust to_pay accordingly to whats left
                to_pay = race.total_pool.sub(race.paid);
            }
			race.paid = race.paid + to_pay;

			if(race.creator == msg.sender) {
				to_pay = to_pay.add(race.bet_matching_pool);
				race.bet_matching_pool = 0;
			}
            claimed[race_id][msg.sender] = true;
			msg.sender.transfer(to_pay);
            emit BetClaimed(race_id, msg.sender, to_pay);
        }
    }

    // the callback function is called by provable when the result is ready
    function __callback(bytes32 _queryId, string memory _result, bytes memory) public override
    {
        require (msg.sender == provable_cbAddress(), "auth failed");
        require(!_processed[_queryId], "Query has already been processed!");
        _processed[_queryId] = true;


		//fetch the roll associated with this query
		uint256 race_id = _query_to_race[_queryId];
		require(race_id > 0, "Invalid _queryId");
		RaceConfig storage race = races[race_id];
		RaceResult storage race_result = races_result[race_id];
        require(race.state != State.REFUND, "Race is already refunded");
		//identify which query is this
        uint256 starts_at = race.start_time + race.betting_duration;
        uint256 ends_at = starts_at + race.race_duration;
		if(_queryId == race.query_price_start) {
            uint32[10] memory prices;
            bool ok;
			(prices, ok) = _extractPrices(_result, race.coins.length, starts_at - config["TIMESTAMP_TOLERANCE"], starts_at + config["TIMESTAMP_TOLERANCE"]);
            if(!ok) {
                emit TimestampsValidationFailed();
                //go to refund
                race.state = State.REFUND;
                emit RaceRefunded(race_id, "Starting price timestamp validation failed");
                return;
            }
            for(uint i = 0; i < race.coins.length; i++) {
                race_result.result_price_start[race.coins[i]] = prices[i];
            }
			// increment state to show we're waiting for the next queries now
			race.state = State(uint(race.state) + 1);
		} else if(_queryId == race.query_price_end) {
			uint32[10] memory prices;
            bool ok;
			(prices, ok) = _extractPrices(_result, race.coins.length, ends_at - config["TIMESTAMP_TOLERANCE"], ends_at + config["TIMESTAMP_TOLERANCE"]);
            if(!ok) {
                emit TimestampsValidationFailed();
                //go to refund
                race.state = State.REFUND;
                emit RaceRefunded(race_id, "Ending price timestamp validation failed");
                return;
            }
            for(uint i = 0; i < race.coins.length; i++) {
                race_result.result_price_end[race.coins[i]] = prices[i];
            }
			// increment state to show we're waiting for the next queries now
			race.state = State(uint(race.state) + 1);
		} else {
			//fatal error
			race.state = State.REFUND;
			emit RaceRefunded(race_id, "Fatal error");
		}
		//if the race state has been incremented enough (2 times), we've finished
		if(race.state == State.DONE) {
            int256[] memory deltas = new int256[](race.coins.length);
            for(uint i = 0; i < deltas.length; i++) {
                deltas[i] = (int256(race_result.result_price_end[race.coins[i]]) - int256(race_result.result_price_start[race.coins[i]])) * int256(PRECISION) / int256(race_result.result_price_start[race.coins[i]]);
            }

            for(uint i = 0; i < deltas.length; i++) {
                bytes32 coin = race.coins[i];
                race_result.coin_won[coin] = true;
                for(uint j = 0; j < deltas.length; j++) {
                    if(i != j) {
                        if(deltas[i] < deltas[j]) {
                            race_result.coin_won[coin] = false;
                        }
                    }
                }

                if(race_result.coin_won[coin]) {
				    race_result.winners_total = race_result.winners_total.add(race.coin_pool[i]);
			    }
            }

            if(race_result.winners_total == 0) {
                //no winners, go to refund
                race.state = State.REFUND;
                emit RaceRefunded(race_id, "No winners");
            } else {
                emit RaceEnded(race_id);
            }
		}
	}

    /**
        @dev Sends the required amount to the cut address
        @param amount Amount to withdraw from the house in Wei
    */
    function withdrawHouse(uint256 amount) external
    {
        require(house >= amount, "Cant withdraw that much");
        house = house.sub(amount);
        IDistributable(config["CUT_DESTINATION"]).distribute.value(amount)();
    }

    /**
        @dev Override the config behavior to change gas price if needed
    */
    function setConfigValue(bytes32 name, uint256 new_value) public override
    onlyOwner() {
        super.setConfigValue(name, new_value);
    }

    /**
        @dev Allows to whitelist or blacklist a coin by symbol
        @param symbol Symbol of the coin
        @param is_whitelisted True to allow, False overwise
    */
    function setWhiteListed(bytes32 symbol, bool is_whitelisted) external
    onlyOwner() {
        whitelisted_coin[symbol] = is_whitelisted;
        emit CoinStatusChanged(symbol, is_whitelisted);
    }

    function getRaceBet(uint256 race_id) external view returns (uint256 length, uint256[10] memory array) {
        length = races[race_id].coins.length;
        for(uint i = 0; i < length; i++) {
            array[i] = _bets[race_id][msg.sender].amount_coin[races[race_id].coins[i]];
        }
        return (length, array);
    }

    function getRaceCoinWon(uint256 race_id) external view returns (uint256 length, bool[10] memory array) {
        length = races[race_id].coins.length;
        for(uint i = 0; i < length; i++) {
            array[i] = races_result[race_id].coin_won[races[race_id].coins[i]];
        }
        return (length, array);
    }

    function getRaceCoinPrices(uint256 race_id, bytes32 coin) external view returns (uint256 start, uint256 end) {
        start = races_result[race_id].result_price_start[coin];
        end = races_result[race_id].result_price_end[coin];
        return (start,end);
    }

    function getRaceCoins(uint256 race_id) external view returns(uint256 length, bytes32[10] memory array, uint256[10] memory pools) {
        length = races[race_id].coins.length;
        for(uint i = 0; i < length; i++) {
            array[i] = races[race_id].coins[i];
            pools[i] = races[race_id].coin_pool[i];
        }
        return (length, array, pools);
    }

    function getBetWithEdge(uint256 amount) external view returns (uint256) {
        return amount.add(amount.div(PRECISION).mul(config["HOUSE_EDGE"]));
    }

    function _placeBet(uint256 race_id, address bettor, uint256[] memory coin_bet) internal {
		RaceConfig storage race = races[race_id];
		Bet storage bet = _bets[race_id][bettor];

        uint256 total_bet = 0;
        for(uint i = 0; i < coin_bet.length; i++) {
            bet.amount_coin[race.coins[i]] = bet.amount_coin[race.coins[i]].add(coin_bet[i]);
            race.coin_pool[i] = race.coin_pool[i].add(coin_bet[i]);
            total_bet = total_bet.add(coin_bet[i]);
        }

		race.total_pool = race.total_pool.add(total_bet);

		emit BetPlaced(race_id, bettor);
	}

    function _prepareNextRace() internal {
		//increment race id for the next one
		current_race = current_race.add(1);
        races[current_race].state = State.READY;
	}

    function _trimString(string memory source, uint posL, uint posR) internal pure returns (string memory result) {
        result = new string(bytes(source).length-posL-posR);
        bytes memory b = bytes(source);
        bytes memory res = bytes(result);
        for(uint i = posL; i < b.length - posR; i++) {
            res[i-posL] = b[i];
        }
        return result;
    }

    function _buildCoinsString(bytes32[] memory coins) internal pure returns (string memory) {
        strings.slice memory result;
        for(uint i = 0; i < coins.length; i++) {
            result = result.concat(strings.toSliceB32(coins[i])).toSlice();
            if(i < coins.length-1) {
                result = result.concat(",".toSlice()).toSlice();
            }
        }
        return result.toString();
    }

    function _extractPrices(string memory entry, uint256 coins_count, uint256 min_timestamp, uint256 max_timestamp) internal pure returns (uint32[10] memory result, bool timestamps_ok) {
        require(coins_count <= 10 && coins_count > 0, "Max 10");

        strings.slice memory sl = entry.toSlice();
        strings.slice memory delim = ",".toSlice();
        string[] memory parts = new string[](coins_count * 2);
        // split every value by comma
        for(uint i = 0; i < parts.length; i++) {
            parts[i] = sl.split(delim).toString();
        }
        // check the prices first
        for(uint i = 0; i < parts.length; i+=2 ) {
            string memory price = _trimString(parts[i],1,0);
            result[i / 2] = uint32(_stringToUintNormalize(price, 3));
        }

        // check the timestamps
        for(uint i = 1; i < parts.length; i+=2 ) {
            string memory stamp;
             if(i == parts.length-1) {
                stamp = _trimString(parts[i],1,1);
            } else {
                stamp = _trimString(parts[i],1,0);
            }
            //read this timestamp
            uint256 timestamp = uint256(_stringToUintNormalize(stamp, 0));
            timestamps_ok = timestamp >= min_timestamp && timestamp <= max_timestamp;
            if(!timestamps_ok) {
                return (result, false);
            }
        }
       return (result, true);
    }

    //ETHORSE CODE
    // utility function to convert string to integer with precision consideration
    function _stringToUintNormalize(string memory s, uint prec) internal pure returns (uint256 result) {
        uint p = prec;
        bool precision = false;
        bytes memory b = bytes(s);
        uint i;
        result = 0;
        for (i = 0; i < b.length; i++) {
            if (precision) {p = p-1;}
            if (uint8(b[i]) == 46){precision = true;}
            uint c = uint8(b[i]);
            if (c >= 48 && c <= 57) {result = result * 10 + (c - 48);}
            if (precision && p == 0){return result;}
        }
        while (p!=0) {
            result = result*10;
            p = p-1;
        }
    }
