pragma solidity 0.5.10;






contract Position is ReentrancyGuard {
    using SafeMath for uint256;
    ERC721 token721;

    enum State {REVIEW, PUBLIC, CANCELLED, SOLD}
    State public state;
    uint256 public positionCreated;
    uint256 public price;
    uint256 public MPFee;
    uint256 public positionFee;

    address payable public seller;
    address payable public marketPlaceAddress;
    address public tokenAddress;
    uint256 public tokenId;

    uint256 constant ONE_HUNDRED = 100e18;

    event PositionCreated(
        address indexed position,
        address indexed seller,
        uint256 indexed tokenId,
        uint256 price,
        address tokenAddress,
        address marketPlaceAddress,
        uint256 timestamp
    );
    event PositionPublished(
        address indexed position,
        address indexed seller,
        uint256 indexed tokenId,
        uint256 price,
        uint256 timestamp
    );
    event PositionCancelled(
        address indexed position,
        address indexed seller,
        uint256 indexed tokenId,
        uint256 price,
        uint256 timestamp
    );
    event PositionBought(
        address indexed position,
        address indexed seller,
        uint256 indexed tokenId,
        uint256 price,
        address buyer,
        uint256 mktFee,
        uint256 sellerProfit,
        uint256 timestamp
    );

    modifier onlySeller() {
        require(msg.sender == seller, "Caller is not seller");
        _;
    }

    modifier onlySold() {
        require(state == State.SOLD, "Position state is not SOLD");
        _;
    }

    modifier onlyReview() {
        require(state == State.REVIEW, "Position state is not REVIEW");
        _;
    }

    modifier onlyPublic() {
        require(state == State.PUBLIC, "Position state is not PUBLIC");
        _;
    }

    constructor(
        address payable _seller,
        uint256 _MPFee,
        uint256 _price,
        address _tokenAddress,
        uint256 _tokenId,
        address payable _marketPlaceAddress
    ) public {
        positionCreated = block.timestamp;
        state = State.REVIEW;

        seller = _seller;
        MPFee = _MPFee;
        price = _price;
        tokenAddress = _tokenAddress;
        tokenId = _tokenId;

        marketPlaceAddress = _marketPlaceAddress;

        token721 = ERC721(tokenAddress);

        emit PositionCreated(
            address(this),
            seller,
            tokenId,
            price,
            tokenAddress,
            marketPlaceAddress,
            block.timestamp
        );
    }

    function cancelPosition() external onlySeller {
        require(state != State.SOLD, "Position already sold");
        setState(State.CANCELLED);
        emit PositionCancelled(address(this), seller, tokenId, price, block.timestamp);
    }

    function publishPosition() external onlySeller {
        require(state == State.REVIEW, "Position already published");
        address approved = token721.getApproved(tokenId);
        require(approved == address(this), "This contract is not approved to use this token");
        setState(State.PUBLIC);
        emit PositionPublished(address(this), seller, tokenId, price, block.timestamp);
    }

    function buyPosition() external payable onlyPublic nonReentrant {
        address approved = token721.getApproved(tokenId);
        require(approved == address(this), "This contract is not approved to use this token");
        require(address(msg.sender).balance >= price, "Buyer does not have enought money");
        require(msg.value >= price, "The value sent is smaller than price");
        uint256 mktFee = price.mul(MPFee).div(ONE_HUNDRED);
        uint256 sellerProfit = price.sub(mktFee);

        setState(State.SOLD);

        seller.transfer(sellerProfit);

        marketPlaceAddress.transfer(mktFee);

        token721.safeTransferFrom(seller, msg.sender, tokenId);

        emit PositionBought(
            address(this),
            seller,
            tokenId,
            price,
            msg.sender,
            mktFee,
            sellerProfit,
            block.timestamp
        );
    }

    function setState(State newState) internal {
        state = newState;
    }

    function getState() public view returns (uint256) {
        return uint256(state);
    }
}

