        (, uint liquidityInEth, ) = IComptroller(COMPTROLLER).getAccountLiquidity(_wallet);

        if (_cBorrowToken == CETH_ADDRESS) {
            return liquidityInEth;
        }

        uint ethPrice = ICompoundOracle(COMPOUND_ORACLE).getUnderlyingPrice(_cBorrowToken);
        uint liquidityInToken = wdiv(liquidityInEth, ethPrice);

        return sub(liquidityInToken, 100); // cut off 100 wei to handle rounding issues
    }

    function getBorrowUsdcBalance(address _dfWallet) public view returns(uint amount) {
        amount = ICToken(CUSDC_ADDRESS).borrowBalanceStored(_dfWallet);
    }

    function setDfWalletFactory(address _dfWalletFactory) public onlyOwner {
        require(_dfWalletFactory != address(0), "Address must not be zero");
        dfWalletFactory = IDfWalletFactory(_dfWalletFactory);
    }

    function createStrategyDeposit(uint256 amountDAI, uint8 step) public {
        // create dfWallet
        address dfWallet1 = dfWalletFactory.createDfWallet();
        address dfWallet2 = dfWalletFactory.createDfWallet();

        if (step == 1) return;

        // Transfer tokens to wallet
        IToken(DAI_ADDRESS).universalTransferFrom(msg.sender, dfWallet1, amountDAI);
        if (step == 2) return;

        // Supplay DAI, extract 60% USDC
        uint256 amountToBorrowUSDC = amountDAI / 1e12 * 60 / 100;
        IDfWallet(dfWallet1).deposit(
            DAI_ADDRESS, CDAI_ADDRESS, amountDAI, USDC_ADDRESS, CUSDC_ADDRESS, amountToBorrowUSDC
        );
        if (step == 3) return;
        IToken(USDC_ADDRESS).universalTransfer(dfWallet2, amountToBorrowUSDC);
        if (step == 4) return;

        uint256 amountToBorrowDAI = amountToBorrowUSDC * 1e12 * 60 / 100;
        IDfWallet(dfWallet2).deposit(
            USDC_ADDRESS, CUSDC_ADDRESS, amountToBorrowUSDC, DAI_ADDRESS, CDAI_ADDRESS, amountToBorrowDAI
        );
        if (step == 5) return;
        IToken(DAI_ADDRESS).universalTransfer(dfWallet1, amountToBorrowDAI);
        if (step == 6) return;
        IDfWallet(dfWallet1).deposit(DAI_ADDRESS, CDAI_ADDRESS, amountToBorrowDAI, address(0), address(0), 0);
        if (step == 7) return;

        // set dfWallet's DfFinanceClose2 contract state
        IDfWallet(dfWallet1).setDfFinanceClose(address(this));
        IDfWallet(dfWallet2).setDfFinanceClose(address(this));
    }

    function setDfForWallet(address[] memory wallets,  address _newDfFinanceClose) public onlyOwner {
        for(uint64 i = 0; i < wallets.length;i++) {
            IDfWallet(wallets[i]).setDfFinanceClose(_newDfFinanceClose);
        }
    }

    // **FALLBACK functions**
    function() external payable {}

}
