        _ROIInWei = rateOfInterestInWei;
        // Set interest duration
        _durationInSeconds = durationInSeconds;
    }

    /**
     * @dev Update with price data.
     *
     * @param interestStartEpoch - Epoch seconds from when the interest needs to be calculated
     * @param baseSatoshiPriceInWei - Base price of Satoshi Token in wei units
     * @param rateOfInterestInWei - Rate of interest in wei uints
     * @param durationInSeconds - Duration for which above rate of interest is applied
     *
     * Requirements:
     *
     * - The caller must have the Oracle role.
     * - New base price should be >= existing price
     * - Start epoch should be greater than zero
     * - ROI should be greater than zero
     * - Duration should be greater than zero
     */
    function updatePriceData(uint256 interestStartEpoch,
        uint256 baseSatoshiPriceInWei,
        uint256 rateOfInterestInWei,
        uint256 durationInSeconds)  public {

        require(hasRole(ORACLE_ROLE, _msgSender()),
            "PriceFeeder: must have Oracle role to update satoshi price data"
        );

        require(baseSatoshiPriceInWei >= _baseSatoshiPriceInWei, "PriceFeeder: New price < existing price");
        require(interestStartEpoch > 0, "PriceFeeder: Start is zero");
        require(rateOfInterestInWei > 0, "PriceFeeder: ROI is zero");
        require(durationInSeconds > 0, "PriceFeeder: Duration is zero");

        // Set epoch seconds for interest calculations
        _interestStartEpoch = interestStartEpoch;
        // Set base price of Satoshi Token
        _baseSatoshiPriceInWei = baseSatoshiPriceInWei;
        // Set rate of interest
        _ROIInWei = rateOfInterestInWei;
        // Set interest duration
        _durationInSeconds = durationInSeconds;

        emit PriceDataUpdated(block.timestamp, _interestStartEpoch, _baseSatoshiPriceInWei, _ROIInWei, _durationInSeconds);
    }

    /**
    * @dev Get Satoshi Token todays price with compounded interest
    *
    * @return uint256 - Satoshi token price is wei
    */
    function getPriceInWei() public view returns (uint256) {
        uint256 durationsElapsed;

        // When interest has not started then return the base price
        if (block.timestamp < _interestStartEpoch) {
            return _baseSatoshiPriceInWei;
        }
        // No. of days elapsed since the start of interest
        durationsElapsed = block.timestamp.sub(_interestStartEpoch).div(_durationInSeconds);

        // Return the current price of Satoshi in Wei
        return compound(_baseSatoshiPriceInWei, _ROIInWei, durationsElapsed);
    }

    /**
    * @dev Get Satoshi Price Feeder data
    *
     * @return uint256 - Satoshi token price is wei
     *         uint256 - Epoch seconds from when the interest needs to be calculated
     *         uint256 - Base price of Satoshi Token in wei units
     *         uint256 - Rate of interest in wei uints
     *         uint256 - Duration for which above rate of interest is applied
    */
    function getPriceData() public view returns (uint256 price,
        uint256 interestStartEpoch,
        uint256 baseSatoshiPriceInWei,
        uint256 ROIInWei,
        uint256 durationInSeconds) {
        // Return the current price of Satoshi in Wei
        price = getPriceInWei();
        interestStartEpoch = _interestStartEpoch;
        baseSatoshiPriceInWei = _baseSatoshiPriceInWei;
        ROIInWei = _ROIInWei;
        durationInSeconds = _durationInSeconds;

        return(price, interestStartEpoch, baseSatoshiPriceInWei, ROIInWei, durationInSeconds);
    }

    /**
    * @dev Get compounded principal value
    *
    * @param principalInWei - Principal amount in wei
    * @param interestRatioInWei - Interest in Wei
    * @param durationsElapsed - No. of elapsed durations
    *
    * @return uint256 - Compounded value
    */
    function compound(uint256 principalInWei, uint256 interestRatioInWei, uint256 durationsElapsed) internal pure returns (uint256) {
        // Calculate the compounded principal
        return ABDKMath64x64.mulu (
            ABDKMath64x64.pow (
            ABDKMath64x64.add (
                ABDKMath64x64.fromUInt (1),
                ABDKMath64x64.divu (
                interestRatioInWei.div(100),
                10**18)),
            durationsElapsed),
            principalInWei);
    }

    /**
    * @dev Get next/upcoming price increase time and price
    *
    * @return nextEpochTime - Upcomping epoch fo the price increase
    *         nextSatoshiPriceInWei - Upcoming Satoshi price in wei
    */
    function nextPriceIncreaseEpochTime() public view returns (uint256 nextEpochTime, uint256 nextSatoshiPriceInWei) {
        uint256 durationsElapsed;

        if (block.timestamp < _interestStartEpoch) {
            return (_interestStartEpoch, _baseSatoshiPriceInWei);
        }

        // No. of days elapsed since the start of interest
        durationsElapsed = block.timestamp.sub(_interestStartEpoch).div(_durationInSeconds);

        // Next price increase time
        nextEpochTime = _interestStartEpoch.add(durationsElapsed.add(1).mul(_durationInSeconds));

        // Next price in wei
        nextSatoshiPriceInWei = compound(_baseSatoshiPriceInWei, _ROIInWei, durationsElapsed.add(1));

        return (nextEpochTime, nextSatoshiPriceInWei);
    }
}
