contract SubToken is ERC20 {
    AssToken public assToken;
    
    constructor (string memory name, string memory symbol) ERC20(name, symbol) public {
        assToken = AssToken(msg.sender);
    }
    
    function mint(address _target, uint256 _amount) public {
        require(msg.sender == address(assToken));
        _mint(_target, _amount);
    }
}

contract StankToken is SubToken("Stank Token", "STNK") {
    string public proposalName;
    uint256 public proposalEndTime;
    string[] public proposalOptions;
    uint256[] public proposalVotes;
    
    address public king;
    address public heir;
    
    constructor (address _king) public {
        king = _king;
    }
    
    event ProposalResolved(string proposalName, uint256 proposalEndTime, string[] proposalOptions, uint256[] proposalVotes);
    
    function withdrawAss(address _target, uint256 _amount) public {
        require(msg.sender == address(assToken));
        assToken.transfer(_target, _amount);
    }
    
    function createProposal(string memory _name, string[] memory _options, uint256 _endTime) public {
        if (proposalEndTime != 0) {
            require(block.timestamp > proposalEndTime, "Current proposal not over");
            emit ProposalResolved(proposalName, proposalEndTime, proposalOptions, proposalVotes);
        }
        require(_endTime > block.timestamp + 1 days, "Proposal must be 1 day in the future");
        require(_endTime < block.timestamp + 7 days, "Proposal must end in less than 7 days");
        require(msg.sender == king, "Only the King may create proposals");
        proposalName = _name;
        proposalEndTime = _endTime;
        proposalOptions = _options;
        proposalVotes = new uint256[](_options.length);
    }
    
    function vote(uint256 _option, uint256 _amount) public {
        require(block.timestamp < proposalEndTime, "No active proposal");
        _burn(msg.sender, _amount);
        proposalVotes[_option] = proposalVotes[_option].add(_amount);
    }
    
    function burnToDestroyFarts(address _account, uint256 _amount) public {
        require(msg.sender == address(assToken));
        _burn(_account, _amount);
    }
    
    function getCurrentProposal() public view returns (string memory _name, uint256 _endTime, string[] memory options, uint256[] memory votes) {
        return (
            proposalName,
            proposalEndTime,
            proposalOptions,
            proposalVotes
        );
    }
    
    function nameHeir(address _heir) public {
        require(msg.sender == king);
        heir = _heir;
    }
    
    function acceptRule() public {
        require(msg.sender == heir);
        king = heir;
        heir = address(0);
    }
}

contract FartToken is SubToken("Fart Token", "FART") {
    function destroy(address _target, uint256 _amount) public {
        require(msg.sender == address(assToken));
        _balances[_target] = _balances[_target].sub(_amount, "ERC20: burn amount exceeds balance");
        _totalSupply = _totalSupply.sub(_amount);
        emit Transfer(_target, address(0), _amount);
    }
    
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal override {
        require(from == address(0), "Farts may not be transferred");
    }
}

contract AssToken is ERC20("Ass Token", "ASS") {
    address public deployer;
    
    StankToken public stankToken;
    FartToken public fartToken;
    
    uint256 constant public assTokenPrice = 5 * 10**16;
    uint256 constant public fartCooldown = 1 days; // Can only fart 1 time a day per account / ass token
    uint256 constant public stankRate = 2 days; // 1 staked Ass produces 1 Stank per 2 days
    
    struct Data {
        uint256 assBalance;
        address lastFartTarget;
        uint256 lastFartTargetFarts;
        uint256 stankBalance;
        uint256 timeTillFart;
        uint256 totalFarts;
        uint256 totalStank;
        uint256 totalStaked;
        uint256 owedStank;
        uint256 totalAssForSale;
        uint256 myFarts;
        string proposalName;
        uint256 proposalEndTime;
        string[] proposalOptions;
        uint256[] proposalVotes;
    }
    
    struct Stake {
        uint256 amount;
        uint256 timeOfStake;
        uint256 owed;
    }
    
    event Fart(address indexed dealer, address indexed victim, uint256 totalTargetFarts);
    
    mapping (address => uint256) public lastFartTime;
    mapping (address => address) public lastFartTarget;
    mapping (address => Stake) public accountStake;
    
    constructor () public {
        deployer = msg.sender;
        _mint(deployer, 1000 * 10**18);
        _mint(address(this), 9000 * 10**18);
        stankToken = new StankToken(msg.sender);
        fartToken = new FartToken();
    }
    
    function purchaseAssTokens(uint256 _amount) public payable {
        require(_amount.mod(10**18) == 0, "Can only purchase whole Ass Tokens");
        require(balanceOf(address(this)) >= _amount, "Token amount not available");
        uint256 _cost = _amount.mul(assTokenPrice).div(10**18);
        uint256 _refund = msg.value.sub(_cost);
        if (_refund > 0) {
            (bool _success,) = msg.sender.call.value(_refund)("");
            require(_success, "Couldn't refund buyer excess ETH");
        }
        _transfer(address(this), msg.sender, _amount);
    }
    
    function fart(address _target) public {
        require(!isLocked(msg.sender), "Farted too recently. Please wait.");
        lastFartTime[msg.sender] = block.timestamp;
        lastFartTarget[msg.sender] = _target;
        fartToken.mint(_target, 10**18);
        emit Fart(msg.sender, _target, fartToken.balanceOf(_target));
    }
    
    function destroyFarts(address _target, uint256 _amount) public {
        uint256 _farts = fartToken.balanceOf(_target);
        uint256 _toDestroy = _amount;
        if (_farts < _amount) {
            _toDestroy = _farts;
        }
        stankToken.burnToDestroyFarts(msg.sender, _toDestroy);
        fartToken.destroy(_target, _toDestroy);
    }
    
    function setStake(uint256 _amount) public {
        Stake memory _stake = accountStake[msg.sender];
        if (_amount == _stake.amount) {
            return;
        }
        uint256 _time = block.timestamp;
        if (_amount > _stake.amount) {
            // If account has stake update owed based on that
            if (_stake.amount > 0) {
                uint256 _timeStaked = _time.sub(_stake.timeOfStake);
                accountStake[msg.sender].owed = _stake.owed.add(_stake.amount.mul(_timeStaked).div(stankRate));
            }
            accountStake[msg.sender].timeOfStake = _time;
            _transfer(msg.sender, address(stankToken), _amount.sub(_stake.amount));
        } else {
            redeemOwedStank(msg.sender);
            stankToken.withdrawAss(msg.sender, _stake.amount.sub(_amount));
        }
        accountStake[msg.sender].amount = _amount;
    }
    
    function redeemOwedStank(address _account) public {
        uint256 _owed = getOwedStank(_account);
        // update owed to 0 and timestamp of stake to now
        accountStake[_account].timeOfStake = block.timestamp;
        accountStake[_account].owed = 0;
        stankToken.mint(_account, _owed);
    }
    
    function getOwedStank(address _account) public view returns (uint256) {
        Stake memory _stake = accountStake[_account];
        uint256 _owed = _stake.owed;
        uint256 _time = block.timestamp;
        uint256 _timeStaked = _time.sub(_stake.timeOfStake);
        return _owed.add(_stake.amount.mul(_timeStaked).div(stankRate));
    }
    
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal override {
        if (isLocked(from)) {
            require(balanceOf(from) >= (amount + 10**18), "Cannot transfer an Ass on cooldown from farting");
        }
    }
    
    function isLocked(address _account) public view returns (bool) {
        uint256 _time = block.timestamp;
        uint256 _timeSinceLastFart = _time.sub(lastFartTime[_account]);
        return _timeSinceLastFart < fartCooldown;
    }
    
    function getData(address _account) public view returns (Data memory) {
        Data memory _data;
        _data.assBalance = balanceOf(_account);
        _data.lastFartTarget = lastFartTarget[_account];
        _data.lastFartTargetFarts = fartToken.balanceOf(_data.lastFartTarget);
        _data.stankBalance = stankToken.balanceOf(_account);
        uint256 _time = block.timestamp;
        uint256 _timeSinceLastFart = _time.sub(lastFartTime[_account]);
        if (_timeSinceLastFart < fartCooldown) {
            _data.timeTillFart = fartCooldown.sub(_timeSinceLastFart);
        }
        _data.totalAssForSale = balanceOf(address(this));
        _data.totalFarts = fartToken.totalSupply();
        _data.totalStank = stankToken.totalSupply();
        _data.totalStaked = balanceOf(address(stankToken));
        _data.owedStank = getOwedStank(_account);
        _data.myFarts = fartToken.balanceOf(_account);
        (_data.proposalName, _data.proposalEndTime, _data.proposalOptions, _data.proposalVotes) = stankToken.getCurrentProposal();
        return _data;
    }
    
    function sweepToken(IERC20 _token) public {
        require(_token != this);
        _token.transfer(deployer, _token.balanceOf(address(this)));
    }
    
    function sweepEth() public {
        (bool _success,) = deployer.call.value(address(this).balance)("");
        require(_success);
    }
}
