// File: @emilianobonassi/gas-saver/ChiGasSaver.sol

pragma solidity ^0.5.0;



contract IFreeFromUpTo is IERC20 {
    function freeFromUpTo(address from, uint256 value) external returns(uint256 freed);
}


contract ChiGasSaver {

    using SafeMath for uint256;

    modifier saveGas(address payable sponsor) {
        uint256 gasStart = gasleft();
        _;
        uint256 gasSpent = 21000 + gasStart - gasleft() + 16 * msg.data.length;

        IFreeFromUpTo chi = IFreeFromUpTo(0x0000000000004946c0e9F43F4Dee607b0eF1fA1c);
        chi.freeFromUpTo(sponsor, (gasSpent + 14154) / 41947);
    }
}

// File: contracts/SNXLinkClaimSaverV2.sol

pragma solidity ^0.5.0;




contract SNXLinkClaimSaverV2 is ChiGasSaver {
    // SNX addresses
    IFeePool public constant SNXProxyFeePool = IFeePool(0xb440DD674e1243644791a4AdfE3A2AbB0A92d309);

    function claimFees()
    external
    saveGas(msg.sender)
    returns (bool) {
        require(SNXProxyFeePool.claimOnBehalf(msg.sender), "Unable to claim fees");
        return true;
    }
}
