// File: contracts/RewardEscrow.sol

pragma solidity ^0.6.0;







contract RewardEscrow is Initializable, OwnableUpgradeSafe, IRewardEscrow {
    using SafeMath for uint256;

    /* The corresponding RewardsToken contract. */
    IERC20 public rewardsToken;

    address public entryManagerAddress;

    /* Lists of (timestamp, quantity, entryTimestamp) pairs per account, sorted in ascending time order.
     * These are the times at which each given quantity of rewardsToken vests. */
    mapping(address => uint256[3][]) public vestingSchedules;

    /* An account's total escrowed rewardsToken balance to save recomputing this for fee extraction purposes. */
    mapping(address => uint256) public totalEscrowedAccountBalance;

    /* An account's total vested reward rewardsToken. */
    mapping(address => uint256) public totalVestedAccountBalance;

    /* The total remaining escrowed balance, for verifying the actual rewardsToken balance of this contract against. */
    uint256 public totalEscrowedBalance;

    /* Escrow period in second */
    uint256 public escrowPeriod;

    uint256 internal constant TIME_INDEX = 0;
    uint256 internal constant QUANTITY_INDEX = 1;
    uint256 internal constant ENTRY_TIME_INDEX = 2;

    /* Limit vesting entries to disallow unbounded iteration over vesting schedules.
     * 5 years */
    uint256 public constant MAX_VESTING_ENTRIES = 52 * 5;

    /* ========== INITIALIZE ========== */
    function initialize(IERC20 _rewardsToken, address _entryManagerAddress)
        public
    {
        __RewardEscrow_init(_rewardsToken, _entryManagerAddress);
    }

    function __RewardEscrow_init(
        IERC20 _rewardsToken,
        address _entryManagerAddress
    ) internal initializer {
        __Context_init_unchained();
        __Ownable_init_unchained();
        __RewardEscrow_init_unchained(_rewardsToken, _entryManagerAddress);
    }

    function __RewardEscrow_init_unchained(
        IERC20 _rewardsToken,
        address _entryManagerAddress
    ) internal initializer {
        rewardsToken = _rewardsToken;
        entryManagerAddress = _entryManagerAddress;
        escrowPeriod = 120;
    }

    /* ========== SETTERS ========== */

    /**
     * @notice set the rewardsToken contract address as we need to transfer rewardsToken when the user vests
     */
    function setRewardsToken(IERC20 _rewardsToken) external onlyOwner {
        rewardsToken = _rewardsToken;
        emit RewardsTokenUpdated(address(_rewardsToken));
    }

    /**
     * @notice set the entryManagerAddress as it is the only authority to be able to call
     * appendVestingEntry with the onlyEntryManager modifer
     */
    function setEntryManagerAddress(address _entryManagerAddress)
        external
        onlyOwner
    {
        entryManagerAddress = _entryManagerAddress;
        emit EntryManagerAddressUpdated(_entryManagerAddress);
    }

    function setEscrowPeriod(uint256 _escrowPeriod) external onlyOwner {
        escrowPeriod = _escrowPeriod;
        emit EscrowPeriodUpdated(_escrowPeriod);
    }

    /* ========== VIEW FUNCTIONS ========== */

    /**
     * @notice A simple alias to totalEscrowedAccountBalance: provides ERC20 balance integration.
     */
    function balanceOf(address account) public override view returns (uint256) {
        return totalEscrowedAccountBalance[account];
    }

    function getAvailableToVest(address account)
        public
        override
        view
        returns (uint256)
    {
        uint256 total;
        for (uint256 i = 0; i < _numVestingEntries(account); i++) {
            uint256 vestingTime = getVestingTime(account, i);
            if (vestingTime > 0 && vestingTime <= now) {
                uint256 qty = getVestingQuantity(account, i);
                if (qty > 0) {
                    total = total.add(qty);
                }
            }
        }
        return total;
    }

    function _numVestingEntries(address account)
        internal
        view
        returns (uint256)
    {
        return vestingSchedules[account].length;
    }

    /**
     * @notice The number of vesting dates in an account's schedule.
     */
    function numVestingEntries(address account)
        external
        override
        view
        returns (uint256)
    {
        return vestingSchedules[account].length;
    }

    /**
     * @notice Get a particular schedule entry for an account.
     * @return A pair of uints: (timestamp, rewardsToken quantity).
     */
    function getVestingScheduleEntry(address account, uint256 index)
        public
        view
        returns (uint256[3] memory)
    {
        return vestingSchedules[account][index];
    }

    /**
     * @notice Get the time at which a given schedule entry will vest.
     */
    function getVestingTime(address account, uint256 index)
        public
        view
        returns (uint256)
    {
        return getVestingScheduleEntry(account, index)[TIME_INDEX];
    }

    /**
     * @notice Get the quantity of rewardsToken associated with a given schedule entry.
     */
    function getVestingQuantity(address account, uint256 index)
        public
        view
        returns (uint256)
    {
        return getVestingScheduleEntry(account, index)[QUANTITY_INDEX];
    }

    /**
     * @notice Get the entry time of a given schedule entry.
     */
    function getVestingEntryTime(address account, uint256 index)
        public
        view
        returns (uint256)
    {
        return getVestingScheduleEntry(account, index)[ENTRY_TIME_INDEX];
    }

    /**
     * @notice Obtain the index of the next schedule entry that will vest for a given user.
     */
    function getNextVestingIndex(address account)
        public
        view
        returns (uint256)
    {
        uint256 len = _numVestingEntries(account);
        uint256 earliestVestingTime;
        uint256 indexOfEarliestVestingTime = len;
        for (uint256 i = 0; i < len; i++) {
            uint256 time = getVestingTime(account, i);
            if (time > 0) {
                if (earliestVestingTime == 0 || time < earliestVestingTime) {
                    earliestVestingTime = time;
                    indexOfEarliestVestingTime = i;
                }
            }
        }
        return indexOfEarliestVestingTime;
    }

    /**
     * @notice Obtain the next schedule entry that will vest for a given user.
     * @return An array of uints: [timestamp, rewardsToken quantity, entry timestamp]. */
    function getNextVestingEntry(address account)
        public
        view
        returns (uint256[3] memory)
    {
        uint256 index = getNextVestingIndex(account);
        if (index == _numVestingEntries(account)) {
            return [uint256(0), 0, 0];
        }
        return getVestingScheduleEntry(account, index);
    }

    /**
     * @notice Obtain the time at which the next schedule entry will vest for a given user.
     */
    function getNextVestingTime(address account)
        external
        view
        returns (uint256)
    {
        return getNextVestingEntry(account)[TIME_INDEX];
    }

    /**
     * @notice Obtain the quantity which the next schedule entry will vest for a given user.
     */
    function getNextVestingQuantity(address account)
        external
        view
        returns (uint256)
    {
        return getNextVestingEntry(account)[QUANTITY_INDEX];
    }

    /**
     * @notice return the full vesting schedule entries vest for a given user.
     * @dev For DApps to display the vesting schedule for the
     * inflationary supply over 5 years. Solidity cant return variable length arrays
     * so this is returning pairs of data. Vesting Time at [0] and quantity at [1] and so on
     */
    function checkAccountSchedule(address account)
        public
        view
        returns (uint256[520] memory)
    {
        uint256[520] memory _result;
        uint256 schedules = _numVestingEntries(account);
        for (uint256 i = 0; i < schedules; i++) {
            uint256[3] memory pair = getVestingScheduleEntry(account, i);
            _result[i * 2] = pair[TIME_INDEX];
            _result[i * 2 + 1] = pair[QUANTITY_INDEX];
        }
        return _result;
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    function _appendVestingEntry(address account, uint256 quantity) internal {
        /* No empty or already-passed vesting entries allowed. */
        require(quantity != 0, "Quantity cannot be zero");

        /* There must be enough balance in the contract to provide for the vesting entry. */
        totalEscrowedBalance = totalEscrowedBalance.add(quantity);
        require(
            totalEscrowedBalance <=
                IERC20(address(rewardsToken)).balanceOf(address(this)),
            "Must be enough balance in the contract to provide for the vesting entry"
        );

        /* Disallow arbitrarily long vesting schedules in light of the gas limit. */
        uint256 scheduleLength = vestingSchedules[account].length;
        require(
            scheduleLength <= MAX_VESTING_ENTRIES,
            "Vesting schedule is too long"
        );

        /* Escrow the tokens for n seconds. */
        uint256 time = now + escrowPeriod * 1 seconds;

        totalEscrowedAccountBalance[account] = totalEscrowedAccountBalance[account]
            .add(quantity);

        if (
            scheduleLength == 0 ||
            getVestingEntryTime(account, scheduleLength - 1) < now - 7 days
        ) {
            vestingSchedules[account].push([time, quantity, now]);
            emit VestingEntryCreated(account, time, quantity);
        } else {
            vestingSchedules[account][scheduleLength - 1][TIME_INDEX] = time;
            uint256 newQuantity = getVestingQuantity(
                account,
                scheduleLength - 1
            )
                .add(quantity);
            vestingSchedules[account][scheduleLength -
                1][QUANTITY_INDEX] = newQuantity;
            emit VestingEntryUpdated(account, time, quantity, newQuantity);
        }
    }

    /**
     * @notice Add a new vesting entry at a given time and quantity to an account's schedule.
     * @dev A call to this should accompany a previous successful call to rewardsToken.transfer(rewardEscrow, amount),
     * to ensure that when the funds are withdrawn, there is enough balance.
     * Note; although this function could technically be used to produce unbounded
     * arrays, it's only withinn the 4 year period of the weekly inflation schedule.
     * @param account The account to append a new vesting entry to.
     * @param quantity The quantity of rewardsToken that will be escrowed.
     */
    function appendVestingEntry(address account, uint256 quantity)
        external
        override
        onlyEntryManager
    {
        _appendVestingEntry(account, quantity);
    }

    /**
     * @notice Allow a user to withdraw any rewardsToken in their schedule that have vested.
     */
    function vest() external override {
        uint256 numEntries = _numVestingEntries(msg.sender);
        uint256 total;
        for (uint256 i = 0; i < numEntries; i++) {
            uint256 vestingTime = getVestingTime(msg.sender, i);
            if (vestingTime > 0 && vestingTime <= now) {
                uint256 qty = getVestingQuantity(msg.sender, i);
                if (qty > 0) {
                    vestingSchedules[msg.sender][i] = [
                        0,
                        0,
                        getVestingEntryTime(msg.sender, i)
                    ];
                    total = total.add(qty);
                }
            }
        }

        if (total != 0) {
            totalEscrowedBalance = totalEscrowedBalance.sub(total);
            totalEscrowedAccountBalance[msg
                .sender] = totalEscrowedAccountBalance[msg.sender].sub(total);
            totalVestedAccountBalance[msg
                .sender] = totalVestedAccountBalance[msg.sender].add(total);
            IERC20(address(rewardsToken)).transfer(msg.sender, total);
            emit Vested(msg.sender, now, total);
        }
    }

    // Added to support recovering Rewards from other systems to be distributed to holders
    function recoverERC20(address tokenAddress, uint256 tokenAmount)
        external
        onlyOwner
    {
        IERC20(tokenAddress).transfer(owner(), tokenAmount);
        emit Recovered(tokenAddress, tokenAmount);
    }

    /* ========== MODIFIERS ========== */

    modifier onlyEntryManager() {
        bool isEntryManager = msg.sender == entryManagerAddress;

        require(
            isEntryManager,
            "Only the entryManager can perform this action"
        );
        _;
    }

    /* ========== EVENTS ========== */

    event RewardsTokenUpdated(address newRewardsToken);

    event EntryManagerAddressUpdated(address newEntryManagerAddress);

    event EscrowPeriodUpdated(uint256 newEscrowPeriod);

    event Vested(address indexed beneficiary, uint256 time, uint256 value);

    event VestingEntryCreated(
        address indexed beneficiary,
        uint256 time,
        uint256 value
    );

    event VestingEntryUpdated(
        address indexed beneficiary,
        uint256 time,
        uint256 newValue,
        uint256 totalValue
    );

    event Recovered(address indexed token, uint256 amount);
}
