contract LinearFormula {
    using SafeMath for uint256;

    uint256 internal constant MU = 0x00000000000000006F05B59D3B200000; //8 * (10**18)
    uint256 internal constant EXAEXA = 0x00C097CE7BC90715B34B9F1000000000; // 10**36
    uint256 internal constant EXA = 0x00000000000000000DE0B6B3A7640000; // 10 ** 18
    uint256 internal constant LAMBDA = 0x00000000000000001BC16D674EC80000; // 2 * (10**18)

    uint256 internal constant MAXSLIPPAGE = 10000;
    uint256 internal constant MINSLIPPAGE = 1;

    // Babylonian method for square root
    // if x is 0, 1, 2, 3 then it is picked up by the first two conditions
    function sqrt(uint256 _x) internal pure returns (uint256) {
        if (_x == 0) return 0;
        else if (_x <= 3) return 1;
        uint256 z = (_x.add(1)).div(2);
        uint256 y = _x;
        while (z < y) {
            y = z;
            z = (_x.div(z).add(z)).div(2);
        }
        return y;
    }

    function percentageOf(uint256 _bps, uint256 _num)
        internal
        pure
        returns (uint256)
    {
        uint256 numTimesBps = _num.mul(_bps);
        require(numTimesBps >= MAXSLIPPAGE, "number is too small");
        require(
            (MINSLIPPAGE <= _bps) && (_bps <= MAXSLIPPAGE),
            "percentage value is invalid"
        );
        return (numTimesBps).div(MAXSLIPPAGE);
    }

    function calculatePurchaseReturn(uint256 _supply, uint256 _depositAmount)
        internal
        pure
        returns (uint256)
    {
        require(_supply > 0, "this token is dead...");
        require(_depositAmount < 500 ether, "oh wow. Thanks, but no");
        if (_depositAmount == 0) {
            return 0;
        }
        // * overflow scenario *
        // supply is 1e18 for 1 NAZ, 1e19 for 10 NAZ
        // 1e20 for 100 NAZ, 1e21 for 1000 NAZ, 1e22 for 10000 NAZ
        // 1e23 for 100k NAZ, 1e24 for 1m NAZ (never will happen)
        // so: 1e24 * 1e24 => 1e48 (half way through to 77 limit)
        // div by lambda and now it is back to 1e24 range. adding won't do much
        // -----------------------
        // * underflow scenario *
        // supply is 1, multiply by 1e18, we get 1e18. divide by LAMBDA
        // we get back zero, deposit amount whatever it is added
        // so we get out deposit amount. no issue
        // -----------------------
        uint256 numerator = _supply.mul(_supply.add(EXA));
        uint256 newTotalValue = numerator.div(LAMBDA).add(_depositAmount);
        // * overflow scenario *
        // 1e36 add to the above 1e24 multiplied by 8e18 to get 1e42
        // sqrt of that roughly halvs it to 1e24 again. No issues
        // ----------------------
        // * underflow scenario *
        // newTotalVAlue is just the deposit amount. we take square root of 1e36
        // number. 1e18 and now divide by two to get 5e17. No issue again
        // ----------------------
        uint256 sqrtArg = EXAEXA.add(newTotalValue.mul(MU));
        uint256 newTokenSupply = (sqrt(sqrtArg).sub(EXA)).div(2);
        return newTokenSupply.sub(_supply);
    }

    function calculateSaleReturn(
        uint256 _supply,
        uint256 _reserveBalance,
        uint256 _sellAmount
    ) internal pure returns (uint256) {
        require(
            _supply > 0 && _reserveBalance > 0 && _sellAmount <= _supply,
            "Check the following: (i) supply must be greater than 0; (ii) reserveBalance must be greater than 0; (iii) you cannot sell more than the supply"
        );
        if (_sellAmount == 0) {
            return 0;
        }
        if (_sellAmount == _supply) {
            return _reserveBalance;
        }
        uint256 supplyWithExa = ((_supply.add(EXA)).sub(_sellAmount));
        uint256 mul2AddSell = supplyWithExa.mul(2).add(_sellAmount);
        return (mul2AddSell.sub(EXA)).mul(_sellAmount).div(LAMBDA);
    }

    // pure doesn't read the storage variables. e.g. sqrt
    // view doesn't modify the storage variables, but can read them (will read them?)
    function notFrontran(
        uint256 _actualAmount,
        uint256 _allowedSlippagePct,
        uint256 _expectedAmount
    ) internal pure returns (bool) {
        // representing percentages in basis points
        // so 0.5 is 50, 0.1 is 10, 0.01 is 1
        // 1% is 100, 10% is 1000 and 100% is 10000
        require(
            (MINSLIPPAGE <= _allowedSlippagePct) &&
                (_allowedSlippagePct <= MAXSLIPPAGE),
            "slippage can be between 1 and 10000 basis points"
        );

        uint256 offset = percentageOf(_allowedSlippagePct, _expectedAmount);
        require(
            _actualAmount >= (_expectedAmount.sub(offset)),
            "try with higher slippage"
        );

        return true;
    }
}

contract LinearCurve is LinearFormula {
    using SafeMath for uint256;

    // these two are implemented in continuous token
    function continuousSupply() public view returns (uint256);

    function reserveBalance() public view returns (uint256);

    function getContinuousMintReward(uint256 _reserveTokenAmount)
        public
        view
        returns (uint256)
    {
        return calculatePurchaseReturn(continuousSupply(), _reserveTokenAmount);
    }

    function getContinuousBurnRefund(uint256 _continuousTokenAmount)
        public
        view
        returns (uint256)
    {
        return
            calculateSaleReturn(
                continuousSupply(),
                reserveBalance(),
                _continuousTokenAmount
            );
    }
}

contract ContinuousToken is Ownable, ERC20, ERC20Detailed, LinearCurve {
    using SafeMath for uint256;

    event Minted(address sender, uint256 amount, uint256 deposit);
    event Burned(address sender, uint256 amount, uint256 refund);

    constructor(
        string memory _name,
        string memory _symbol,
        uint8 _decimals,
        uint256 _initialSupply
    ) public ERC20Detailed(_name, _symbol, _decimals) LinearCurve() {
        _mint(msg.sender, _initialSupply);
    }

    function _continuousMint(
        uint256 _deposit,
        uint256 _slippage,
        uint256 _expectedAmount
    ) internal returns (uint256) {
        require(_deposit > 0, "Deposit must be non-zero.");
        uint256 rewardAmount = getContinuousMintReward(_deposit);

        // front run check
        bool wasNotFrontran = notFrontran(
            rewardAmount,
            _slippage,
            _expectedAmount
        );
        require(wasNotFrontran, "try with higher slippage");

        _mint(msg.sender, rewardAmount);

        emit Minted(msg.sender, rewardAmount, _deposit);
        return rewardAmount;
    }

    function _continuousBurn(
        uint256 _amount,
        uint256 _reserve,
        uint256 _slippage,
        uint256 _expectedAmount
    ) internal returns (uint256) {
        require(_amount > 0, "Amount must be non-zero.");
        // ! THIS IS SUPER IMPORTANT !
        require(
            balanceOf(msg.sender) >= _amount,
            "no money - no honey friend, don't be hacking me"
        );

        uint256 refundAmount = getContinuousBurnRefund(_amount);
        // i.e. we need that the refundAmount is strictly LESS than the reserve
        require(refundAmount < _reserve, "don't be hacking, friend");

        // front run check
        bool wasNotFrontran = notFrontran(
            refundAmount,
            _slippage,
            _expectedAmount
        );
        require(wasNotFrontran, "try with higher slippage");

        _burn(msg.sender, _amount);

        emit Burned(msg.sender, _amount, refundAmount);
        return refundAmount;
    }

    function continuousSupply() public view returns (uint256) {
        return totalSupply();
    }
}

contract NazToken is ContinuousToken, ReentrancyGuard {
    uint256 internal reserve;

    constructor(
        string memory _name,
        string memory _symbol,
        uint8 _decimals,
        uint256 _initialSupply
    )
        public
        payable
        ContinuousToken(_name, _symbol, _decimals, _initialSupply)
    {
        reserve = msg.value;
    }

    function() external payable nonReentrant {
        uint256 purchaseAmount = msg.value;
        uint256 rewardAmount = getContinuousMintReward(purchaseAmount);
        _continuousMint(purchaseAmount, 100, rewardAmount);
        reserve = reserve.add(purchaseAmount);
    }

    function mint(uint256 _slippage, uint256 _expectedAmount)
        public
        payable
        nonReentrant
    {
        uint256 purchaseAmount = msg.value;
        _continuousMint(purchaseAmount, _slippage, _expectedAmount);
        reserve = reserve.add(purchaseAmount);
    }

    function burn(
        uint256 _amount,
        uint256 _slippage,
        uint256 _expectedAmount
    ) public nonReentrant {
        uint256 refundAmount = _continuousBurn(
            _amount,
            reserve,
            _slippage,
            _expectedAmount
        );
        reserve = reserve.sub(refundAmount);
        msg.sender.transfer(refundAmount);
    }

    function reserveBalance() public view returns (uint256) {
        return reserve;
    }
}
