        return calacReleaseStartTimeLongForInputAmount(investAmount);
    }
    //for check
    function calacReleaseStartTimeLongForInputAmount(uint256 _investAmount) public pure returns (uint256){
        //y = 86400*40-86400*1500000*10^36/(x^2+100*x*10^18+50000*10^36)
        //never overflow because invest amount will never reach the point
        return 3456000-(129600000000000000000000000000000000000000000000/(_investAmount**2+_investAmount*100000000000000000000+50000000000000000000000000000000000000000));
    }

    function calacAllReleaseTimeLong() public view returns (uint256){
        return calacReleaseStartTimeLong().add(RELEASE_TIME_LONG);
    }    
