    }
    function _increaseApprove(uint256 amount) internal{
        bool approveResult;
        (approveResult,)=insTokenAddr.call(abi.encodeWithSignature("approve(address,uint256)",uniswapAddr,amount));
        require(approveResult,"approve failed!");
    }
