  uint256 _rate = 40000;
  uint256 _cap  = 621539400000000000000;
  mapping(address => uint256) public contributions;

  constructor(
    IERC20 _token,
    address  _walletAddress,
    address payable _fundAddress,
    uint256 _openingTime,
    uint256 _closingTime
  )
    Crowdsale(_rate, _fundAddress, _token)
    CappedCrowdsale(_cap)
    AllowanceCrowdsale(_walletAddress)
    TimedCrowdsale(_openingTime, _closingTime)
    public
  {

  }

  /**
  * @dev Returns the amount contributed so far by a sepecific user.
  * @param _beneficiary Address of contributor
  * @return User contribution so far
  */
  function getContribution(address _beneficiary)
    public view returns (uint256)
  {
    return contributions[_beneficiary];
  }

  /**
  * @dev Extend parent behavior requiring purchase to respect the beneficiary's funding cap.
  * @param beneficiary Token purchaser
  * @param weiAmount Amount of wei contributed
  */
  function _preValidatePurchase(address beneficiary, uint256 weiAmount) internal view {
      uint256 newContribution = contributions[beneficiary].add(weiAmount);
      require(tx.gasprice <= maxGasPrice,"TonCrowdsale: beneficiary's max Gas Price exceeded");
      require(newContribution <= investorHardCap, "TonCrowdsale: beneficiary's max cap exceeded");
      require(newContribution >= investorMinCap, "TonCrowdsale: beneficiary's min cap not reached");
      super._preValidatePurchase(beneficiary, weiAmount);
