pragma solidity 0.6.10;

// import "@openzeppelin/contracts/math/SafeMath.sol";
// import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
// import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";

contract TempusVesting {
    using SafeMath for uint256;

    IERC20 internal token;
    IUniswapV2Pair internal uniswapPair;
    bool public hasDepositedTempus = false;
    bool public hasDepositedLP = false;

    address public owner;
    address public crowdsale;
    uint256 public depositedTempusAmount;
    uint256 public depositedLPAmount;
    uint256 public withdrawnToken = 0;
    uint256 public withdrawnLP = 0;
    uint256 public constant VESTING_PERIOD = 1036800; // 6 months of blocks
    uint256 public finalBlock;

    modifier onlyOwner() {
        require(msg.sender == owner || msg.sender == crowdsale, "TempusVesting: caller is not the owner");
        _;
    }

    constructor(
        IERC20 _token,
        IUniswapV2Pair _uniswap,
        address _crowdsale
    ) public {
        owner = msg.sender;
        crowdsale = _crowdsale;
        token = IERC20(_token);
        uniswapPair = IUniswapV2Pair(_uniswap);
    }

    function depositTempus(uint256 amount) external {
        _depositTempus(amount);
    }

    function depositLP(uint256 amount) external {
        _depositLP(amount);
    }

    function _depositTempus(uint256 amount) internal onlyOwner {
        require(!hasDepositedTempus, "TempusVesting: already deposited temp");
        token.transferFrom(msg.sender, address(this), amount);
        depositedTempusAmount = amount;
        finalBlock = block.number.add(VESTING_PERIOD);
        hasDepositedTempus = true;
    }

    function _depositLP(uint256 amount) internal onlyOwner {
        require(!hasDepositedLP, "TempusVesting: already deposited lp tokens");
        uniswapPair.transferFrom(msg.sender, address(this), amount);
        depositedLPAmount = amount;
        finalBlock = block.number.add(VESTING_PERIOD);
        hasDepositedLP = true;
    }

    function withdrawTempus() external onlyOwner {
        require(hasDepositedTempus, "TempusVesting: have not deposited any temp");

        if (block.number > finalBlock) {
            hasDepositedTempus = false;
            token.transfer(owner, token.balanceOf(address(this)));
        } else {
            uint256 numerator = depositedTempusAmount.mul(VESTING_PERIOD.sub(finalBlock.sub(block.number)));
            uint256 allowedAmount = numerator.div(VESTING_PERIOD);
            uint256 toWithdraw = allowedAmount.sub(withdrawnToken);
            withdrawnToken = withdrawnToken.add(toWithdraw);
            token.transfer(owner, toWithdraw);
        }
    }

    function withdrawLP() external onlyOwner {
        require(hasDepositedLP, "TempusVesting: have not deposited any lp tokens");

        if (block.number > finalBlock) {
            hasDepositedLP = false;
            uniswapPair.transfer(owner, uniswapPair.balanceOf(address(this)));
        } else {
            uint256 numerator = depositedLPAmount.mul(VESTING_PERIOD.sub(finalBlock.sub(block.number)));
            uint256 allowedAmount = numerator.div(VESTING_PERIOD);
            uint256 toWithdraw = allowedAmount.sub(withdrawnLP);
            withdrawnLP = withdrawnLP.add(toWithdraw);
            uniswapPair.transfer(owner, toWithdraw);
        }
    }
}
