pragma solidity ^0.6.0;


contract SBTCCrowdsale {
    using SafeMath for uint256;
    using Address for address;
    
    constructor() public { owner = msg.sender; }
    address payable owner;
    
    uint256 public individualMinCap = 1 ether;
    uint256 public individualMaxCap = 5 ether;
    uint256 public crowdsaleHardCap = 262.5 ether;
    uint256 private _weiRaised;

    receive() external payable {
        uint256 weiAmount = msg.value;
        require(weiAmount <= individualMaxCap, "Too much ether transferred (max = 5 ETH)");
        require(weiAmount >= individualMinCap, "Too little ether transferred (min = 1 ETH)");
        require(_weiRaised.add(weiAmount) <= crowdsaleHardCap, "Crowdsale hard cap has been hit");
        
        // update state
        _weiRaised = _weiRaised.add(weiAmount);
    }
    
    function contractOwner() public view returns (address) {
        return owner;
    }
    
    function weiRaised() public view returns (uint256) {
        return _weiRaised;
    }
    
    function withdraw(uint256 amount) public virtual {
        require(owner.send(amount));
    }
    
}
