interface IMiner {
  function initINSS(address token) external;
}

contract INSS is ERC20 , ERC20Detailed("Interstellars", "INSS", 18) {

  uint constant MAX_SUPPLY = 100000 * 10 ** 18; 

  address private immutable miner;

  constructor(address m) public {
    miner = m;
    IMiner(m).initINSS(address(this));
  }

  function mint(address user, uint amount) external {
    require(msg.sender == miner, "invalid miner");
    if(totalSupply() >= MAX_SUPPLY) {
      return ;
    }

    if(amount.add(totalSupply()) >= MAX_SUPPLY) {
      _mint(user, MAX_SUPPLY.sub(totalSupply()));
    } else {
      _mint(user, amount);
    }

  }

  function burn(uint256 amount) public {
    _burn(msg.sender, amount);
  }

  function burnFrom(address account, uint256 amount) public {
    _burnFrom(account, amount);
  }
}
