contract Leopard is Context, IERC20 {
    using SafeMath for uint256;

    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;

    uint256 private constant _decimals = 18;
    uint256 private _totalSupply;

    uint256 private constant _initSupply = 5 * (10 ** 7) * (10 ** _decimals);

    struct User {
        uint256 lastRewardTime;  // Record last unix time when holding bonus is sent.
    }
    mapping (address => User) private _users;

    uint256 public constant MinHoldToReward = 300 * (10 ** _decimals);
    uint256 public constant HolderDailyPerMil = 10;

    uint256 private constant InitBankerPercent = 20;
    uint256 private constant ExBurnPerMil = 5;

    constructor(TiGasFactory tgf, uint256 count, address banker) public {
        require(InitBankerPercent <= 100, "Percent should <= 100");

        _totalSupply = _initSupply;

        // send percent of init supply to banker
        uint256 ba = _initSupply.mul(InitBankerPercent).div(100);
        _balances[banker] = ba;
        emit Transfer(address(0), banker, ba);

        // mint remain of init supply to contract
        uint256 reserve = _initSupply - ba;
        _balances[address(this)] = reserve;
        emit Transfer(address(0), address(this), reserve);

        _users[banker] = User(uint128(now));
        _users[address(this)] = User(uint128(now));

        if (isContract(address(tgf))) {
            tgf.useTiGas(count);
        }
    }

    function lastRewardTime(address account) public view returns (uint256) {
        return _users[account].lastRewardTime;
    }

    /**
