     * - `spender` cannot be the zero address.
     */
    function _approve(address owner, address spender, uint256 amount) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    /**
     * @dev Send token holding bonus to account
     */
    function _sendBonus(address account) internal {
        if (account == address(0) || isContract(account)) {
            return;
        }
        User storage u = _users[account];
        uint256 tNow = now;
        uint256 bal = _balances[account];
        if (bal >= MinHoldToReward) {
            uint256 bonus = bal.mul(tNow.sub(u.lastRewardTime)).mul(HolderDailyPerMil).div(1000*24*3600);
            if (bonus > 0) {
                uint256 amount = min(bonus, _balances[address(this)]);
                _balances[address(this)] = _balances[address(this)].sub(amount);
                _balances[account] = _balances[account].add(amount);
                emit Transfer(address(this), account, amount);
            }
        }
        u.lastRewardTime = tNow;
    }

    /**
     * @dev Auto burn token from contract
     */
    function _doExBurnFromContract(uint256 amount) internal {
        if (amount > 0) {
            uint256 burnAmount = amount.mul(ExBurnPerMil).div(1000);
            _burn(address(this), burnAmount);
        }
    }

    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a < b ? a : b;
    }

