// File: contracts/EIP712MetaTransaction.sol

pragma solidity 0.5.17;



contract EIP712MetaTransaction is EIP712Base {
    using SafeMath for uint256;
    bytes32 private constant META_TRANSACTION_TYPEHASH = keccak256(
        bytes(
            "MetaTransaction(uint256 nonce,address from,bytes functionSignature)"
        )
    );

    event MetaTransactionExecuted(
        address userAddress,
        address payable relayerAddress,
        bytes functionSignature
    );
    mapping(address => uint256) nonces;

    /*
     * Meta transaction structure.
     * No point of including value field here as if user is doing value transfer then he has the funds to pay for gas
     * He should call the desired function directly in that case.
     */
    struct MetaTransaction {
        uint256 nonce;
        address from;
        bytes functionSignature;
    }

    function executeMetaTransaction(
        address userAddress,
        bytes memory functionSignature,
        bytes32 sigR,
        bytes32 sigS,
        uint8 sigV
    ) public payable returns (bytes memory) {
        MetaTransaction memory metaTx = MetaTransaction({
            nonce: nonces[userAddress],
            from: userAddress,
            functionSignature: functionSignature
        });
        require(
            verify(userAddress, metaTx, sigR, sigS, sigV),
            "Signer and signature do not match"
        );

        nonces[userAddress] = nonces[userAddress].add(1);
        emit MetaTransactionExecuted(
            userAddress,
            msg.sender,
            functionSignature
        );
        // Append userAddress and relayer address at the end to extract it from calling context
        (bool success, bytes memory returnData) = address(this).call(
            abi.encodePacked(functionSignature, userAddress)
        );

        require(success, "Function call not successfull");
        
        return returnData;
    }

    function hashMetaTransaction(MetaTransaction memory metaTx)
        internal
        view
        returns (bytes32)
    {
        return
            keccak256(
                abi.encode(
                    META_TRANSACTION_TYPEHASH,
                    metaTx.nonce,
                    metaTx.from,
                    keccak256(metaTx.functionSignature)
                )
            );
    }

    function getNonce(address user) public view returns (uint256 nonce) {
        nonce = nonces[user];
    }

    function verify(
        address signer,
        MetaTransaction memory metaTx,
        bytes32 sigR,
        bytes32 sigS,
        uint8 sigV
    ) internal view returns (bool) {
        return
            signer ==
            ecrecover(
                toTypedMessageHash(hashMetaTransaction(metaTx)),
                sigV,
                sigR,
                sigS
            );
    }

    function _msgSender() internal view returns (address payable sender) {
        if(msg.sender == address(this)) {
            bytes memory array = msg.data;
            uint256 index = msg.data.length;
            assembly {
                // Load the 32 bytes word from memory with the address on the lower 20 bytes, and mask those.
                sender := and(mload(add(array, index)), 0xffffffffffffffffffffffffffffffffffffffff)
            }
        } else {
            sender = msg.sender;
        }
        return sender;
    }
}

// File: contracts/EthereumDeposit.sol

pragma solidity 0.5.17;




contract EthereumDeposit is AdminRole, EIP712MetaTransaction {

    uint256 public maxLimit = 2 ether;

    address payable public wallet;
    

    //Tells if a transaction has been claimed
    mapping(bytes32 => bool) public txVsClaimed;

    event Deposit(uint256 amount);
    event Withdraw(uint256 amount, bytes32 indexed txHash);
    event Released(uint256 amount);

    constructor (address payable account) public {
        wallet = account;
        _initializeEIP712("BadBit.Games", "0.1");
    }

    //This will be used for deposit
    function () external payable {
        require(msg.value <= maxLimit, "Value greater then max limit");
        emit Deposit(msg.value);
    }

    function withdraw(
        bytes32 txHash,
        address payable account,
        uint256 amount
    )
        external
        onlyAdmin
    {
        if (txVsClaimed[txHash] == true) {
            //ignore the tx
            return;
        }
        txVsClaimed[txHash] = true;
        account.transfer(amount);
        emit Withdraw(amount, txHash);
    }

    function changeMaxLimit(uint256 newLimit) external onlyAdmin {
        maxLimit = newLimit;
    }

    function releaseFunds(uint256 amount) external onlyAdmin {
        emit Released(amount);
        wallet.transfer(amount);
    }
}
