// File: contracts/TellorProxy.sol


interface ITellor {
  function addTip(uint256 _requestId, uint256 _tip) external;
  function submitMiningSolution(string calldata _nonce, uint256 _requestId, uint256 _value) external;
  function submitMiningSolution(string calldata _nonce,uint256[5] calldata _requestId, uint256[5] calldata _value) external;
  function depositStake() external;
  function requestStakingWithdraw() external;
  function withdrawStake() external;
  function vote(uint256 _disputeId, bool _supportsDispute) external;
}


contract TellorProxy is ChiGasSaver, Ownable, ERC20 {

  address tellorAddress;   // Address of Tellor Oracle
  address gasToken;        // Gas token address to use for gas saver
  uint256 stakedAt;        // Timestamp when the stake was deposited
  uint256 unstakedAt;      // Timestamp when the stake was withdrawn
  uint256 fee;             // Fee that stakers collect, where 100 is 1%
  uint256 stakeAmount;     // The Tellor token staking amount
  uint256 feesCollected;   // The fees collected for the stakers

  constructor(address _tellorAddress,
    address _gasToken,
    uint256 _fee,
    uint256 _stakeAmount)
    public
    // NOTE: Change the token name and symbol for each staked miner
    // Use the form: TPS-YYYYMM-D
    // Where YYYY == year, MM == month, and D = duration (e.g. 1 month)
    ERC20("Tellorpool Token", "TPS-202011")
  {
    tellorAddress = _tellorAddress;
    gasToken = _gasToken;
    fee = _fee;
    stakeAmount = _stakeAmount * 1e18;
    feesCollected = 0;
  }

  // Enter the contract, get TPS for your TRB
  function enter(uint256 _amount) public {
    require(IERC20(tellorAddress).transferFrom(msg.sender, address(this), _amount));
    _mint(msg.sender, _amount);
    require(totalSupply() <= stakeAmount, "CAN NOT ACCEPT MORE THAN STAKE AMOUNT");
  }

  // Leave the contract, get TRB for your TPS
  function leave() public {
    // Can only leave after the contract has been unstaked or before staking
    require(unstakedAt != 0 || stakedAt == 0, "NOT UNSTAKED YET");
    uint256 totalTRB = IERC20(tellorAddress).balanceOf(address(this));
    uint256 totalShares = totalSupply();
    uint256 theirShares = balanceOf(msg.sender);
    uint256 theirTRB = totalTRB.mul(theirShares).div(totalShares);
    _burn(msg.sender, theirShares);
    require(IERC20(tellorAddress).transfer(msg.sender, theirTRB));
  }

  // Close the contract, send remaining balance to owner
  function close() external onlyOwner {
    // Can only close after being unstaked for 90 days
    // or after 365 days from staking (in case of dispute)
    require((unstakedAt < now - 90 days && unstakedAt != 0) || (stakedAt < now - 365 days && stakedAt != 0), "CAN NOT CLOSE YET");
    uint256 leftovers = IERC20(tellorAddress).balanceOf(address(this));
    require(IERC20(tellorAddress).transfer(owner(), leftovers));
  }

  // Withdraw rewards less fees
  function withdrawRewards() external onlyOwner {
    require(unstakedAt == 0, "NO WITHDRAWS AFTER UNSTAKING");
    require(stakedAt != 0, "NO WITHDRAWS BEFORE STAKING");
    uint256 surplus = IERC20(tellorAddress).balanceOf(address(this)) - stakeAmount;
    uint256 available = surplus.mul(10000 - fee).div(10000);
    feesCollected += surplus - available;
    require(IERC20(tellorAddress).transfer(owner(), available));
  }

  // Returns the TRB value of the token with 5 decimals of percision
  function getTokenValue() external view returns (uint256) {
    if (stakedAt != 0) {
      return (stakeAmount + feesCollected).mul(100000).div(totalSupply());
    } else {
      return 100000;
    }
  }

  // Returns the fee as a percentage with 2 decimals of percision
  function getFee() external view returns (uint256) {
    return fee;
  }

  // Withdraw for other tokens if not Tellor
  function tokenWithdraw(address _tokenAddress, uint256 _amount)
    external
    onlyOwner
  {
    require(_tokenAddress != tellorAddress, "CAN NOT WITHDRAW TRB");
    require(IERC20(_tokenAddress).transfer(owner(), _amount));
  }

  function addTip(uint256 _requestId, uint256 _tip)
    external
    onlyOwner
    saveGas(msg.sender, gasToken)
  {
    ITellor(tellorAddress).addTip(_requestId, _tip);
  }

  function submitMiningSolutionSaveGas(string calldata _nonce, uint256 _requestId, uint256 _value)
    external
    onlyOwner
    saveGas(msg.sender, gasToken)
  {
    ITellor(tellorAddress).submitMiningSolution(_nonce, _requestId, _value);
  }

  function submitMiningSolutionSaveGas(string calldata _nonce, uint256[5] calldata _requestId, uint256[5] calldata _value)
    external
    onlyOwner
    saveGas(msg.sender, gasToken)
  {
    ITellor(tellorAddress).submitMiningSolution(_nonce, _requestId, _value);
  }

  function submitMiningSolution(string calldata _nonce, uint256 _requestId, uint256 _value)
    external
    onlyOwner
  {
    ITellor(tellorAddress).submitMiningSolution(_nonce, _requestId, _value);
  }

  function submitMiningSolution(string calldata _nonce, uint256[5] calldata _requestId, uint256[5] calldata _value)
    external
    onlyOwner
  {
    ITellor(tellorAddress).submitMiningSolution(_nonce, _requestId, _value);
  }

  function depositStake()
    external
    onlyOwner
  {
    stakedAt = now;
    ITellor(tellorAddress).depositStake();
  }

  function requestStakingWithdraw()
    external
    onlyOwner
  {
    ITellor(tellorAddress).requestStakingWithdraw();
  }

  function withdrawStake()
    external
    onlyOwner
  {
    unstakedAt = now;
    ITellor(tellorAddress).withdrawStake();
  }

  function vote(uint256 _disputeId, bool _supportsDispute)
    external
    onlyOwner
  {
    ITellor(tellorAddress).vote(_disputeId, _supportsDispute);
  }

}
