pragma solidity 0.6.12;










contract LpBar is ERC20("LpBar", "xLP"), Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    IERC20 public lp;
    IERC20 public sashimi;
    IMasterChef public masterChef;
    uint256 public pid;
    IStakingRewards public uniStake;
    uint256 public totalLp;
    IUni public uni;
    IUniswapV2Router02 router;

    uint256 public perShareForUniHarvest;
    uint256 public perShareForChefHarvest;
    address public wETH;

    mapping(address => uint256) public lpBalance;
    mapping(address => uint256) public xLPBalance; // address => amount
    mapping(address => uint256) public rewardDebtsFromUni; // address => sashimi reward debt
    mapping(address => uint256) public rewardDebtsFromChef; // address => sashimi reward debt

    constructor(IERC20 _sashimi, IERC20 _lp, IUni _uni, IMasterChef _masterChef, IUniswapV2Router02 _router, IStakingRewards _uniStake, address _wETH) public {
        require(_uniStake.stakingToken() == address(_lp), "Staking token not matched.");
        lp = _lp;
        masterChef = _masterChef;
        uniStake = _uniStake;
        sashimi = _sashimi;
        uni = _uni;
        router = _router;
        wETH = _wETH;
    }

    function setPid(uint256 _pid) public onlyOwner{
        require(pid == 0, "Pid already set.");
        pid = _pid;
        approveMax();
    }

    function approveMax() public onlyOwner{
        uni.approve(address(router), type(uint256).max);
        this.approve(address(masterChef), type(uint256).max);
        lp.approve(address(uniStake), type(uint256).max);
    }

    function enter(uint256 _amount) public {
        updatePerShareForUniHarvest();
        lp.transferFrom(msg.sender, address(this), _amount);
        uint256 lpBal = lpBalance[msg.sender];

        uint256 sashimiHarvestFromUni = 0;
        if (lpBal > 0)
        {
            uint256 debt = rewardDebtsFromUni[msg.sender];
            sashimiHarvestFromUni = lpBal.mul(perShareForUniHarvest).div(1e12).sub(debt);
        }
        // stake lp
        uniStake.stake(_amount);

        // update rewardDebtsFromUnit
        lpBal = lpBal.add(_amount);
        rewardDebtsFromUni[msg.sender] = lpBal.mul(perShareForUniHarvest).div(1e12);
        // update balance
        lpBalance[msg.sender] = lpBal;

        uint xLpAmount = _amount;
        uint256 totalShares = totalSupply();
        totalLp = totalLp.add(_amount);

        // mint lpx
        _mint(address(this), xLpAmount);
        updatePerShareForChefHarvest(xLpAmount, true, totalShares);

        uint256 xLpBal = xLPBalance[msg.sender];
        uint256 sashimiHarvestFromChef = 0;
        if (xLpBal > 0)
        {
            uint256 debt = rewardDebtsFromChef[msg.sender];
            sashimiHarvestFromChef = xLpBal.mul(perShareForChefHarvest).div(1e12).sub(debt);
        }

        xLpBal = xLpBal.add(xLpAmount);
        xLPBalance[msg.sender] = xLpBal;
        // update rewardDebtsFromChef
        rewardDebtsFromChef[msg.sender] = xLpBal.mul(perShareForChefHarvest).div(1e12);
        transferSashimi(msg.sender, sashimiHarvestFromUni.add(sashimiHarvestFromChef));
    }

    function leave(uint256 _xLpShare) public {
        uint256 xLpBal = xLPBalance[msg.sender];
        require(xLpBal >= _xLpShare, "Insufficient balance.");

        updatePerShareForUniHarvest();
        uint256 lpBal = lpBalance[msg.sender];
        uint256 debtFromUni = rewardDebtsFromUni[msg.sender];
        uint256 sashimiHarvestFromUni = lpBal.mul(perShareForUniHarvest).div(1e12).sub(debtFromUni);

        // withdraw lpx
        uint256 totalShares = totalSupply();
        updatePerShareForChefHarvest(_xLpShare, false, totalShares);
        uint256 debtFromChef = rewardDebtsFromChef[msg.sender];
        uint256 sashimiHarvestFromChef = xLpBal.mul(perShareForChefHarvest).div(1e12).sub(debtFromChef);

        transferSashimi(msg.sender, sashimiHarvestFromUni.add(sashimiHarvestFromChef));

        uint256 returnLpAmount = _xLpShare;

        if (returnLpAmount > 0)
        {
            // transfer lp
            uniStake.withdraw(returnLpAmount);
            lp.transfer(msg.sender, returnLpAmount);
        }

        // update lp balance
        lpBal = lpBal.sub(returnLpAmount);
        lpBalance[msg.sender] = lpBal;
        totalLp = totalLp.sub(returnLpAmount);

        // update rewardDebtsFromUnit
        rewardDebtsFromUni[msg.sender] = lpBal.mul(perShareForUniHarvest).div(1e12);

        // update rewardDebtsFromUnit
        xLpBal = xLpBal.sub(_xLpShare);
        xLPBalance[msg.sender] = xLpBal;
        _burn(address(this), _xLpShare);
        rewardDebtsFromChef[msg.sender] = xLpBal.mul(perShareForChefHarvest).div(1e12);
    }

    function changeRouter(IUniswapV2Router02 _router) onlyOwner public{
        router = _router;
        uni.approve(address(_router), type(uint256).max);
    }

    function harvestFromUniStake() private returns (uint256){
        uint256 beforeBalance = uni.balanceOf(address(this));
        uniStake.getReward();
        uint256 afterBalance = uni.balanceOf(address(this));
        return afterBalance.sub(beforeBalance);
    }

    function updatePerShareForUniHarvest() private{
        uint256 curPerShare = perShareForUniHarvest;
        if (totalLp == 0)
            return;

        uint256 uniHarvest = harvestFromUniStake();
        if (uniHarvest > 0)
        {
            address[] memory path = new address[](3);
            path[0] = address(uni);
            path[1] = wETH;
            path[2] = address(sashimi);
            uint[] memory amounts = router.swapExactTokensForTokens(uniHarvest, 0, path, address(this), block.timestamp + 3);
            uint256 reward = amounts[amounts.length - 1];
            perShareForUniHarvest = curPerShare.add(reward.mul(1e12).div(totalLp));
        }
    }

    function updatePerShareForChefHarvest(uint256 _amount, bool _deposit, uint256 _totalShares) private {
        uint256 beforeBalance = sashimi.balanceOf(address(this));

        if (_deposit)
        {
            // deposit lpx
            masterChef.deposit(pid, _amount);
        }
        else
            masterChef.withdraw(pid, _amount);

        uint256 afterBalance = sashimi.balanceOf(address(this));
        uint256 chefHarvest = afterBalance.sub(beforeBalance);

        if (_totalShares > 0 && chefHarvest > 0)
        {
            uint256 curPerShare = perShareForChefHarvest;
            perShareForChefHarvest = curPerShare.add(chefHarvest.mul(1e12).div(_totalShares));
        }
    }

    function transferSashimi(address receipt, uint256 amount) private {
        if (amount > 0)
        {
            uint256 bal = IERC20(sashimi).balanceOf(address(this));
            sashimi.transfer(receipt, amount > bal ? bal : amount);
        }
    }

    function earned(address account) public view returns(uint256){
        uint256 totalShares = totalSupply();
        if (totalShares == 0)
            return 0;
        uint256 earnedUni = uniStake.earned(address(this));
        address[] memory path = new address[](3);
        path[0] = address(uni);
        path[1] = wETH;
        path[2] = address(sashimi);
        uint256[] memory amounts = router.getAmountsOut(earnedUni, path);

        uint256 updatePerShareForUniHarvest = perShareForUniHarvest.add(amounts[amounts.length - 1].mul(1e12).div(totalLp));
        uint256 debtFromUni = rewardDebtsFromUni[account];
        uint256 lpBal = lpBalance[account];
        uint256 sashimiHarvest1 = lpBal.mul(updatePerShareForUniHarvest).div(1e12).sub(debtFromUni);

        uint256 earnedChef = masterChef.pendingSashimi(pid, address(this));
        uint256 updatePerShareForChefHarvest = perShareForChefHarvest.add(earnedChef.mul(1e12).div(totalShares));
        uint256 debtFromChef = rewardDebtsFromChef[account];
        uint256 xLpBal = xLPBalance[account];
        uint256 sashimiHarvest2 = xLpBal.mul(updatePerShareForChefHarvest).div(1e12).sub(debtFromChef);
        return sashimiHarvest1.add(sashimiHarvest2);
    }
}
