/* ========== Uniswap wrapper ========== */

interface Uniswap {
    function WETH() external pure returns (address);
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);
    function getAmountsIn(uint amountOut, address[] calldata path) external view returns (uint[] memory amounts);

    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapExactTokensForETH(
        uint amountIn, 
        uint amountOutMin, 
        address[] calldata path, 
        address to, 
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapExactETHForTokens(
        uint amountOutMin, 
        address[] calldata path, 
        address to, 
        uint deadline
    ) external payable returns (uint[] memory amounts);
}

contract ExchangeWithUniswap is Ownable {
    using SafeERC20 for IERC20;
    using SafeMath for uint;
    
    address public uniAddr = address(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
    
    function setUniAddr(address _uni) public onlyOwner {
        uniAddr = _uni;
    }
    
    function exchangeTokenForToken(address[] memory path, uint _amountIn) internal returns (uint _amountOut) {
            
        IERC20(path[0]).safeApprove(uniAddr, 0);
        IERC20(path[0]).safeApprove(uniAddr, _amountIn);
        
        uint[] memory amounts = Uniswap(uniAddr).swapExactTokensForTokens(_amountIn, uint(0), path, address(this), block.timestamp.add(600));
        _amountOut = amounts[2];
    }

    function exchangeTokenForETH(address _from, uint _amountIn) internal returns (uint _amountOut) {
        IERC20(_from).safeApprove(uniAddr, 0);
        IERC20(_from).safeApprove(uniAddr, _amountIn);
        
        address[] memory path = new address[](2);
        path[0] = _from;
        path[1] = Uniswap(uniAddr).WETH();
        
        uint[] memory amounts = Uniswap(uniAddr).swapExactTokensForETH(_amountIn, uint(0), path, address(this), block.timestamp.add(600));
        _amountOut = amounts[1];
    }

    function exchangeETHForToken(address _to, uint _amountIn) internal returns (uint _amountOut) {
        
        address[] memory path = new address[](2);
        path[0] = Uniswap(uniAddr).WETH();
        path[1] = _to;
        
        uint[] memory amounts = Uniswap(uniAddr).swapExactETHForTokens{value: _amountIn}(uint(0), path, address(this), block.timestamp.add(600));
        _amountOut = amounts[1];
    }
}


/* ========== Mining pool wrapper ========== */

abstract contract PoolWrapper is ERC20, Ownable, ReentrancyGuard{

    using SafeERC20 for IERC20;
    using SafeMath for uint;

    string  public poolName;
    address public poolCoin;

    address public feeTo;
    address public controller;
    uint public feeRate = 500;

    event Deposit(uint indexed _investAmount, uint indexed _shareAmount, address sender);
    event Withdraw(uint indexed _shareAmount, uint indexed _withdrawAmount, address sender);

    constructor(string memory _poolName, address _poolCoin) internal {
        feeTo = _msgSender();
        poolName = _poolName;
        poolCoin = _poolCoin;
    }
    
    modifier safeApprove(address _coin, address _to, uint _amount) {
        IERC20(_coin).safeApprove(_to, 0);
        IERC20(_coin).safeApprove(_to, _amount);
        _;
    }

    function setController(address _controller) public onlyOwner {
        require(_controller != address(0), "invalid controller address");
        controller = _controller;
    }
    
    function setFeeTo(address _feeTo) public onlyOwner {
        require(_feeTo != address(0), "invalid feeTo address");
        feeTo = _feeTo;
    }

    function setFeeRate(uint _feeRate) public onlyOwner {
        require(_feeRate >= 0 && _feeRate <= 1000, "invalid feeRate");
        feeRate = _feeRate;
    }

    function deposit(uint _amount) public nonReentrant {
        require(_amount > 0, "invalid amount");
        IERC20(poolCoin).safeTransferFrom(_msgSender(), address(this), _amount); 

        // claim        
        uint UnderlyingCoinAmountBeforeClaim = IERC20(poolCoin).balanceOf(address(this));
        _claim();
        uint underlyingCoinAmountClaimed = IERC20(poolCoin).balanceOf(address(this)).sub(UnderlyingCoinAmountBeforeClaim);
        
        // send fee
        if ( underlyingCoinAmountClaimed > 0 && _msgSender() != controller) {
            uint claimFee = underlyingCoinAmountClaimed.mul(feeRate).div(10000);
            IERC20(poolCoin).safeTransfer(feeTo, claimFee);
            underlyingCoinAmountClaimed = underlyingCoinAmountClaimed.sub(claimFee);
        }

        // deposit and stake
        uint lpAmount = _deposit(_amount.add(underlyingCoinAmountClaimed));
        _stake(lpAmount);
        uint investLpAmount = lpAmount.mul(_amount).div(_amount.add(underlyingCoinAmountClaimed));
        
        // calculate share
        uint shares;
        if(totalSupply() == 0) {
            shares = investLpAmount;
        } else {
            uint totalLpBalance = _stakeBalance();
            shares = investLpAmount.mul(totalSupply()).div(totalLpBalance.sub(investLpAmount));
        }

        // mint share
        _mint(_msgSender(), shares);     
        emit Deposit(_amount, shares, _msgSender());
    }

    function withdraw(uint _amount) public nonReentrant {
        require(_amount > 0 && balanceOf(_msgSender()) >= _amount, "invalid _amount or insufficient balance");

        // claim
        uint UnderlyingCoinAmountBeforeClaim = IERC20(poolCoin).balanceOf(address(this));
        _claim();
        uint underlyingCoinAmountClaimed = IERC20(poolCoin).balanceOf(address(this)).sub(UnderlyingCoinAmountBeforeClaim);
        
        if (underlyingCoinAmountClaimed > 0) {
            // send fee
            if(_msgSender() != controller) {
                uint claimFee = underlyingCoinAmountClaimed.mul(feeRate).div(10000);
                IERC20(poolCoin).safeTransfer(feeTo, claimFee);
                underlyingCoinAmountClaimed = underlyingCoinAmountClaimed.sub(claimFee);
            }
            // deposit and stake
            uint lpAmount = _deposit(underlyingCoinAmountClaimed);
            _stake(lpAmount);
        }
        
        // calculate value of share
        uint totalLpBalance = _stakeBalance();
        uint unstakeLpAmountExpected = totalLpBalance.mul(_amount).div(totalSupply());
        
        // withdraw
        uint UnderlyingCoinAmountBeforeWithdraw = IERC20(poolCoin).balanceOf(address(this));
        uint unstakeLpAmountFactual = _unstake(unstakeLpAmountExpected);
        _withdraw(unstakeLpAmountFactual);
        uint withdrawUnderlyingCoinAmount = IERC20(poolCoin).balanceOf(address(this)).sub(UnderlyingCoinAmountBeforeWithdraw);

        // burn share
        _burn(_msgSender(), _amount);
        IERC20(poolCoin).safeTransfer(_msgSender(), withdrawUnderlyingCoinAmount);
        emit Withdraw(_amount, withdrawUnderlyingCoinAmount, _msgSender());
    }

    /* ========== internal function ========== */
    
    function _deposit(uint) internal virtual returns (uint) { 
        revert("need override");
    }

    function _stake(uint) internal virtual {
        revert("need override");
    }

    function _unstake(uint) internal virtual returns (uint)  {
        revert("need override");
    }
    
    function _withdraw(uint) internal virtual {    
        revert("need override");
    }

    function _claim() internal virtual returns (uint) {
        revert("need override");
    }

    function _stakeBalance() internal view virtual returns (uint) {
        revert("need override");
    } 
}

/* ========== Harvest wrapper ========== */
interface IHarvest {
    function deposit(uint amount) external;     // deposit
    function withdraw(uint256 amount) external; // withdraw
    function stake(uint256 amount) external;    // stake
    // function withdraw(uint256 amount) external; // unstake
    function getReward() external;
    function balanceOf(address account) external view returns (uint256);
}

abstract contract HarvestWrapper is PoolWrapper, ExchangeWithUniswap {
    
    address public reward;
    address public coin;
    address public lp;
    address public deposit;
    address public stake;
    address public intermediator;

    function setAddr(
        address _reward, 
        address _coin, 
        address _lp, 
        address _deposit, 
        address _stake, 
        address _intermediator
    ) public onlyOwner {
        require(_reward != address(0) 
            && _coin != address(0) 
            && _lp != address(0) 
            && _deposit != address(0) 
            && _stake != address(0) 
            && _intermediator != address(0), "invalid address"
        );
        reward          = _reward;
        coin            = _coin;
        lp              = _lp;
        deposit         = _deposit;
        stake           = _stake;
        intermediator   = _intermediator;
    }

    function _deposit(uint _amount) 
        override 
        internal 
        safeApprove(coin, deposit, _amount) 
        returns (uint) 
    { 
        IHarvest(deposit).deposit(_amount);
        return IERC20(lp).balanceOf(address(this));
    }

    function _withdraw(uint _amount) 
        override 
        internal 
        safeApprove(lp, deposit, _amount) 
    {    
        IHarvest(deposit).withdraw(_amount);
    }

    function _stake(uint _amount) 
        override 
        internal 
        safeApprove(lp, stake, _amount) 
    {
        IHarvest(stake).stake(_amount);
    }

    function _unstake(uint _amount) 
        override 
        internal 
        returns (uint) 
    {
        IHarvest(stake).withdraw(_amount);
        return IERC20(lp).balanceOf(address(this));
    }

    function _claim() 
        override 
        internal 
        returns (uint) 
    {
        
        IHarvest(stake).getReward();
        uint rewardBalance = IERC20(reward).balanceOf(address(this));
        if (rewardBalance > 0) {
            address[] memory path = new address[](3);
            path[0] = reward;
            path[1] = intermediator;
            path[2] = coin;
            return exchangeTokenForToken(path, rewardBalance);
        }
    }

    function _stakeBalance() 
        override 
        internal 
        view 
        returns (uint) 
    {
        return IHarvest(stake).balanceOf(address(this));
    }

}

