contract Middleware is Ownable, Pausable {

    address public CHI_TOKEN;
    address public UPTRENND_TOKEN;

    modifier discountCHI {
        uint256 gasStart = gasleft();
        _;
      uint256 gasSpent = 21000 + gasStart - gasleft() + 16 * msg.data.length;
        ICHIToken(CHI_TOKEN).freeFromUpTo(msg.sender, (gasSpent + 14154) / 41947);
    }

    constructor() public {
        CHI_TOKEN=0x0000000000004946c0e9F43F4Dee607b0eF1fA1c;
        UPTRENND_TOKEN=0x07597255910a51509CA469568B048F2597E72504;
    }

    function batchTransfer(address[] calldata _recipients,uint256[] calldata _amounts) discountCHI whenNotPaused onlyOwner external{
        for(uint i=0;i<_recipients.length;i++){
            IERC20(UPTRENND_TOKEN).transfer(_recipients[i],_amounts[i]);
        }
    }

    function withdraw() onlyOwner whenNotPaused external{
        uint256 balance=IERC20(UPTRENND_TOKEN).balanceOf(address(this));
        IERC20(UPTRENND_TOKEN).transfer(msg.sender,balance);
        _pause();
    }

    function pauseContract() onlyOwner whenNotPaused external{
        _pause();
    }

    function unpauseContract() onlyOwner whenPaused external{
        _unpause();
    }
}
