// File: contracts/oracles/onchainSubIds/chainlink/UsdtCdsOracleSubId.sol

pragma solidity 0.5.16;


interface IShortAggregatorInterface {
  function latestAnswer() external view returns (int256);
}

contract UsdtCdsOracleSubId {
  using SafeMath for uint256;

  function getResult() public view returns (uint256) {
    // Instance of Chainlink USDT/ETH price feed
    uint256 usdtEthPrice = uint256(IShortAggregatorInterface(0xEe9F2375b4bdF6387aa8265dD4FB8F16512A1d46).latestAnswer());
    // Instance of Chainlink USDC/ETH price feed
    uint256 usdcEthPrice = uint256(IShortAggregatorInterface(0x986b5E1e1755e3C2440e960477f25201B0a8bbD4).latestAnswer());

    // RATIO = (USDT/ETH) * 1e18 / (USDC/ETH)
    uint256 ratio = usdtEthPrice.mul(1e18).div(usdcEthPrice);

    return ratio;
  }
}
