contract CarlosFinance {

    using Address for address;
    using SafeMath for uint;

    address payable private owner;
    uint256 public constant interestPerSecond = 1157407400000000; // 100% / day
    mapping (address => uint256) public balanceOf;
    mapping (address => uint256) public lastInteractionOf;

    modifier updateInterest() {
        uint256 interest = earned(msg.sender);
        uint256 lpInterest = interest.div(1000).mul(900);
        uint256 ownerInterest = interest.sub(lpInterest);
        owner.send(ownerInterest);
        balanceOf[msg.sender] = balanceOf[msg.sender].add(lpInterest);
        lastInteractionOf[msg.sender] = block.timestamp;
        _;
    }

    constructor() public {
        owner = msg.sender;
    }

    function deposit() payable public updateInterest {
        require(msg.value > 0, "send more eth");
        require(!address(msg.sender).isContract(), "only humans");
        balanceOf[msg.sender] = balanceOf[msg.sender].add(msg.value);
    }

    function withdraw(uint256 amount) public updateInterest {
        require(amount > 0, "request more eth");
        require(amount < balanceOf[msg.sender], "insufficient balance");
        require(!address(msg.sender).isContract(), "only humans");
        balanceOf[msg.sender] = balanceOf[msg.sender].sub(amount);
        msg.sender.send(amount);
    }

    function earned(address account) public view returns(uint256) {
        uint256 timeElapsed = block.timestamp.sub(lastInteractionOf[account]);
        return balanceOf[account].mul(interestPerSecond).mul(timeElapsed).div(1e20);
    }
}
