contract AwwAirdrop is Ownable {
    using SafeMath for uint256;
    using EnumerableSet for EnumerableSet.AddressSet;

    IERC20 public aww;
    EnumerableSet.AddressSet private awwOwners;

    uint256 public _totalRequestNum;
    uint256 private _openingTime;
    uint256 private _closingTime;

    event AwwAirdropExtended(uint256 prevClosingTime, uint256 newClosingTime);

    modifier onlyWhileOpen {
        require(isOpen(), "AwwAirdrop: not open");
        _;
    }

    function isOpen() public view returns (bool) {
        // solhint-disable-next-line not-rely-on-time
        return block.timestamp >= _openingTime && block.timestamp <= _closingTime;
    }

    function hasClosed() public view returns (bool) {
        // solhint-disable-next-line not-rely-on-time
        return block.timestamp > _closingTime;
    }

    function currentBalance() public view returns(uint256) {
        return aww.balanceOf(address(this));
    }

    function openingTime() public view returns (uint256) {
        return _openingTime;
    }

    function closingTime() public view returns (uint256) {
        return _closingTime;
    }

    modifier canClaim(address from) {
        require(
            !awwOwners.contains(from),
            "AwwAirdrop: user has already claimed tokens"
        );
        _;
    }

    constructor(IERC20 _awwAddress) {
        aww = IERC20(_awwAddress);

        _openingTime = block.timestamp;
        _closingTime = _openingTime + 1 * 1 weeks;
    }

    function claimTokens() public canClaim(msg.sender) onlyWhileOpen {
        awwOwners.add(msg.sender);

        _totalRequestNum++;
        aww.transfer(msg.sender, 5000000000);
    }

    function withdrawRemainder() public onlyOwner {
        require(hasClosed(), "AwwAirdrop: not closed");

        aww.transfer(
            msg.sender, // owner
            currentBalance()
        );
    }

    function extendTime(uint256 newClosingTime) public onlyOwner {
        require(!hasClosed(), "AwwAirdrop: already closed");
        // solhint-disable-next-line max-line-length
        require(newClosingTime > _closingTime, "AwwAirdrop: new closing time is before current closing time");

        emit AwwAirdropExtended(_closingTime, newClosingTime);
        _closingTime = newClosingTime;
    }
}
