// File: contracts/sale.sol

pragma solidity ^0.6.0;






contract EurSale is OwnableUpgradeSafe, ReentrancyGuardUpgradeSafe {
    using SafeMath for uint256;

    AggregatorV3Interface internal eurUsdFeed;
    AggregatorV3Interface internal ethUsdFeed;

    bool public saleOngoing;

    event LogPurchase(address buyer, uint256 amountIn, uint256 amountOut);

    mapping(address => uint256) public purchaseHistory; // user => EUR history
    address payable public treasury;
    address public euroAddress;

    constructor(address eurUsdFeed_, address ethUsdFeed_, address payable treasury_, address euro_) public {
        OwnableUpgradeSafe.__Ownable_init();
        ReentrancyGuardUpgradeSafe.__ReentrancyGuard_init();

        eurUsdFeed = AggregatorV3Interface(eurUsdFeed_);
        ethUsdFeed = AggregatorV3Interface(ethUsdFeed_);
        saleOngoing = false;
        treasury = treasury_; // euro treasury
        euroAddress = euro_;
    }

    /**
     * Returns the latest price
     */
    function getLatestEthEurPrice() public view returns (uint256) {
        (
            uint80 roundID, 
            int price,
            uint startedAt,
            uint timeStamp,
            uint80 answeredInRound
        ) = eurUsdFeed.latestRoundData();

        (
            uint80 roundID2, 
            int price2,
            uint startedAt2,
            uint timeStamp2,
            uint80 answeredInRound2
        ) = ethUsdFeed.latestRoundData();
        // If the round is not complete yet, timestamp is 0
        require(timeStamp > 0 && timeStamp2 > 0, "Rounds not complete");
        return uint256(price2).mul(10 ** 8).div(uint256(price)); // 10 ** 8 price
    }

    function purchaseEUR() nonReentrant public payable returns (bool) {
        require(saleOngoing, "Sale must be ongoing");
        require(msg.value > 0, "Must send Eth");
        uint256 currentBalance = IERC20(euroAddress).balanceOf(address(this));

        uint256 inputEth = msg.value; // 10 ^ 18
        uint256 ethEurPrice = getLatestEthEurPrice(); // 10 ^ 8

        uint256 outputEUR = (inputEth.div(ethEurPrice)).mul(10 ** 4); // 10 ^ 9

        require(outputEUR <= currentBalance, "Insufficient EUR funds");

        // update purchase history
        purchaseHistory[msg.sender] = purchaseHistory[msg.sender].add(outputEUR);

        // send ETH
        treasury.transfer(msg.value);

        // send EUR
        SafeERC20.safeTransfer(IERC20(euroAddress), msg.sender, outputEUR);

        emit LogPurchase(msg.sender, inputEth, outputEUR);
        return true;
    }

    function startSale() 
        external
        onlyOwner
    {
        require(!saleOngoing, "Sale must not be ongoing");
        saleOngoing = true;
    }

    // withdraw to delete
    function withdrawPrincipal()
        external
        onlyOwner
    {
        require(!saleOngoing, "Sale must not be ongoing");
        uint256 currentBalance = IERC20(euroAddress).balanceOf(address(this));
        SafeERC20.safeTransfer(IERC20(euroAddress), msg.sender, currentBalance);
    }

    function endSale()
        external
        onlyOwner
    {
        require(saleOngoing, "Sale must be ongoing to end");
        saleOngoing = false;
    }
}
