/**
 * @title SafeMath
 * @dev Math operations with safety checks that revert on error
 */
library SafeMath {

  /**
  * @dev Multiplies two numbers, reverts on overflow.
  */
  function mul(uint256 a, uint256 b) internal pure returns (uint256) {
    // Gas optimization: this is cheaper than requiring 'a' not being zero, but the
    // benefit is lost if 'b' is also tested.
    // See: https://github.com/OpenZeppelin/openzeppelin-solidity/pull/522
    if (a == 0) {
      return 0;
    }

    uint256 c = a * b;
    require(c / a == b);

    return c;
  }

  /**
  * @dev Integer division of two numbers truncating the quotient, reverts on division by zero.
  */
  function div(uint256 a, uint256 b) internal pure returns (uint256) {
    require(b > 0); // Solidity only automatically asserts when dividing by 0
    uint256 c = a / b;
    // assert(a == b * c + a % b); // There is no case in which this doesn't hold

    return c;
  }

  /**
  * @dev Subtracts two numbers, reverts on overflow (i.e. if subtrahend is greater than minuend).
  */
  function sub(uint256 a, uint256 b) internal pure returns (uint256) {
    require(b <= a);
    uint256 c = a - b;

    return c;
  }

  /**
  * @dev Adds two numbers, reverts on overflow.
  */
  function add(uint256 a, uint256 b) internal pure returns (uint256) {
    uint256 c = a + b;
    require(c >= a);

    return c;
  }

  /**
  * @dev Divides two numbers and returns the remainder (unsigned integer modulo),
  * reverts when dividing by zero.
  */
  function mod(uint256 a, uint256 b) internal pure returns (uint256) {
    require(b != 0);
    return a % b;
  }
}

pragma solidity ^0.6.0;

contract StakeLimit {
    
    using SafeMath for uint256;
    
    // Check if amount of lp tokens is less than $20000
    function countStakeAmount(address _unipair, address _usdtpair, uint256 liquiditybalance) public view returns(uint256, uint256, uint256, uint256) {
        return countEthAmount(_unipair, _usdtpair, liquiditybalance);
    }
    
    function countEthAmount(address _unipair, address _usdtpair, uint256 liquiditybalance) internal view returns(uint256, uint256, uint256, uint256) {
        address unitoken0 = IUniswapV2Pair(_unipair).token0();
        (uint112 unireserves0, uint112 unireserves1, ) = IUniswapV2Pair(_unipair).getReserves();
        uint256 uniTotalSupply = IERC20(_unipair).totalSupply();
        uint256 ethAmountForChill;
        uint256 tokenbalance;

        if(unitoken0 != 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2) {
            tokenbalance = liquiditybalance.mul(unireserves0) / uniTotalSupply;
            ethAmountForChill = UniswapV2Library.getAmountOut(tokenbalance, unireserves0, unireserves1);
        } else {
            tokenbalance = liquiditybalance.mul(unireserves1) / uniTotalSupply;
            ethAmountForChill = UniswapV2Library.getAmountOut(tokenbalance, unireserves1, unireserves0);
        }
        (uint256 usdtoutamt, uint256 totalamt) = countUsdtAmount(ethAmountForChill, _usdtpair);
        return (tokenbalance, ethAmountForChill, usdtoutamt, totalamt);
    }

    function countUsdtAmount(uint256 ethAmountForChill, address _usdtpair) internal view returns(uint256, uint256) {
        address usdttoken0 = IUniswapV2Pair(_usdtpair).token0();
        (uint112 usdtreserves0, uint112 usdtreserves1, ) = IUniswapV2Pair(_usdtpair).getReserves();

        uint256 usdtOutAmount;
        if (usdttoken0 == 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2) { // WETH Mainnet
            usdtOutAmount = UniswapV2Library.getAmountOut(1e18, usdtreserves0, usdtreserves1);
        } else {
            usdtOutAmount = UniswapV2Library.getAmountOut(1e18, usdtreserves1, usdtreserves0);
        }
        uint256 totalAmount = ((ethAmountForChill.div(1e18)).mul(usdtOutAmount.div(1e6))).mul(2);
        return (usdtOutAmount, totalAmount);
    }
}
