contract RopeUniSpindle is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many tokens the user has provided.
        uint256 uniRewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of HOPEs
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accHopePerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws tokens to a pool. Here's what happens:
        //   1. The pool's `accHopePerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    // Info of each pool.
    struct PoolInfo {
        IERC20 token; // Address of token contract.
        IStakingRewards uniStaking; // Address of the uni staking
        uint256 lastRewardBlock; // Last block number that UNI distribution occurs.
        uint256 accUniPerShare; // Accumulated UNIs per share, times 1e12.
        uint256 supply; // Total tokens in the pool
    }

    // tokens we're farming
    address public constant uni = 0x1f9840a85d5aF5bf1D1762F925BDADdC4201F984;

    // weth
    address public constant weth = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;

    // dex
    address public univ2Router2 = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
    // Record whether the pair has been added.
    mapping(address => uint256) public tokenPID;

    HopeBooster public hopeBooster;
    IERC20 public rope;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);

    constructor(IERC20 _ropeAddress, HopeBooster _hopeBoosterAddress) public {
        rope = _ropeAddress;
        hopeBooster = _hopeBoosterAddress;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // Add a new token to the pool. Can only be called by the owner.
    // XXX DO NOT add the same token more than once. Rewards will be messed up if you do.
    function add(IERC20 _token, IStakingRewards _uniStaking) public onlyOwner {
        require(tokenPID[address(_token)] == 0, "Duplicate add.");
        poolInfo.push(
            PoolInfo({
        token: _token,
        uniStaking: _uniStaking,
        lastRewardBlock: block.number,
        accUniPerShare: 0,
        supply: 0
        })
        );
        tokenPID[address(_token)] = poolInfo.length;
    }

    // Withdraw tokens from multiple pools
    function massWithdraw(uint256[] memory _pids) public {
        // We batch the handling of all rewards collection here, to reduce gas fee
        loadAllRopesIntoBooster();

        for (uint i=0; i < _pids.length; i++) {
            // As we handled the rewards at the beginning, we dont need to worry about it
            _withdrawWithoutRewards(i);
        }
    }

    function exit() public {
        // We batch the handling of all rewards collection here, to reduce gas fee
        loadAllRopesIntoBooster();

        uint256 length = poolInfo.length;
        for (uint256 i = 0; i < length; ++i) {
            // As we handled the rewards at the beginning, we dont need to worry about it
            _withdrawWithoutRewards(i);
        }
    }

    // Update reward variables for all pools. Be careful of gas spending!
    function massUpdatePool() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];

        if (block.number <= pool.lastRewardBlock) {
            return;
        }

        uint256 tokenSupply = pool.supply;

        if (tokenSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }

        uint256 uniReward = pool.uniStaking.earned(address(this));
        pool.uniStaking.getReward();

        pool.accUniPerShare = pool.accUniPerShare.add(uniReward.mul(1e12).div(tokenSupply));
        pool.lastRewardBlock = block.number;
    }

    // Deposit LP tokens to pool for HOPE allocation.
    function deposit(uint256 _pid, uint256 _amount) public {
        require(_amount > 0, "Amount to deposit must be > 0");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);

        uint256 pendingUni = user.amount.mul(pool.accUniPerShare).div(1e12).sub(user.uniRewardDebt);
        user.amount = user.amount.add(_amount);
        pool.supply = pool.supply.add(_amount);
        user.uniRewardDebt = user.amount.mul(pool.accUniPerShare).div(1e12);

        if (pendingUni > 0) {
            _loadRopesIntoBooster(msg.sender, pendingUni);
        }

        pool.token.safeTransferFrom(msg.sender, address(this), _amount);

        //approve and stake to uniswap
        pool.token.approve(address(pool.uniStaking), _amount);
        pool.uniStaking.stake(_amount);

        emit Deposit(msg.sender, _pid, _amount);
    }

    // Withdraw tokens from pool.
    function withdraw(uint256 _pid, uint256 _amount) public {
        require(_amount >= 0, "Amount to withdraw must be >= 0");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(_pid);

        uint256 pendingUni = user.amount.mul(pool.accUniPerShare).div(1e12).sub(user.uniRewardDebt);
        user.amount = user.amount.sub(_amount);
        pool.supply = pool.supply.sub(_amount);
        user.uniRewardDebt = user.amount.mul(pool.accUniPerShare).div(1e12);

        if (pendingUni > 0) {
            _loadRopesIntoBooster(msg.sender, pendingUni);
        }

        //unstake uniswap lp token from uniswap
        pool.uniStaking.withdraw(_amount);

        pool.token.safeTransfer(msg.sender, _amount);

        emit Withdraw(msg.sender, _pid, _amount);
    }

    function loadAllRopesIntoBooster() public {
        loadAllRopesOfAddressIntoBooster(msg.sender);
    }

    function loadAllRopesOfAddressIntoBooster(address _addr) public {
        uint256 totalPendingUni = 0;

        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            UserInfo storage user = userInfo[pid][_addr];

            if (user.amount == 0) {
                continue;
            }

            PoolInfo storage pool = poolInfo[pid];

            updatePool(pid);

            uint256 pendingUni = user.amount.mul(pool.accUniPerShare).div(1e12).sub(user.uniRewardDebt);
            user.uniRewardDebt = user.amount.mul(pool.accUniPerShare).div(1e12);

            totalPendingUni = totalPendingUni.add(pendingUni);
        }

        if (totalPendingUni > 0) {
            _loadRopesIntoBooster(_addr, totalPendingUni);
        }
    }

    function _loadRopesIntoBooster(address _addr, uint256 _amount) internal {
        uint256[] memory amounts = _swap(address(uni), address(rope), _amount);

        rope.safeApprove(address(hopeBooster), amounts[amounts.length - 1]);
        hopeBooster.deposit(_addr, amounts[amounts.length - 1], false);
    }

    function _swap(address _from, address _to, uint256 _amount) internal returns (uint256[] memory amounts) {
        // Swap with uniswap
        IERC20(_from).safeApprove(univ2Router2, 0);
        IERC20(_from).safeApprove(univ2Router2, _amount);

        address[] memory path;

        if (_from == weth || _to == weth) {
            path = new address[](2);
            path[0] = _from;
            path[1] = _to;
        } else {
            path = new address[](3);
            path[0] = _from;
            path[1] = weth;
            path[2] = _to;
        }

        uint256 minAmount = 0;

        return UniswapRouterV2(univ2Router2).swapExactTokensForTokens(
            _amount,
            minAmount,
            path,
            address(this),
            now.add(60)
        );
    }

    function _withdrawWithoutRewards(uint256 _pid) internal returns(uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        if (user.amount == 0) {
            return 0;
        }

        uint256 _amount = user.amount;
        user.amount = 0;
        pool.supply = pool.supply.sub(_amount);
        user.uniRewardDebt = 0;

        //unstake lp token from uniswap
        pool.uniStaking.withdraw(_amount);

        pool.token.safeTransfer(msg.sender, _amount);
        emit Withdraw(msg.sender, _pid, _amount);
        return _amount;
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) public {
        _withdrawWithoutRewards(_pid);
    }
}
