contract uniswapQuery  {
    using SafeMath for uint;

    address public  _uniswapV2FactoryAddress = 0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f;
    address public  _uniswapV2Router02Address = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    

    function out(address pair,address out_token,uint liquidity) public view returns(uint){
        (uint112 _reserve0, uint112 _reserve1,) = IUniswapV2Pair(pair).getReserves(); // gas savings
        address _token0 = IUniswapV2Pair(pair).token0();                                // gas savings
        address _token1 = IUniswapV2Pair(pair).token1();                                // gas savings
        uint balance0 = IERC20(_token0).balanceOf(pair);
        uint balance1 = IERC20(_token1).balanceOf(pair);

        
        uint _totalSupply = IUniswapV2Pair(pair).totalSupply(); // gas savings, must be defined here since totalSupply can update in _mintFee
        uint amount0 = liquidity.mul(balance0) / _totalSupply; // using balances ensures pro-rata distribution
        uint amount1 = liquidity.mul(balance1) / _totalSupply; // using balances ensures pro-rata distribution
        
        address[] memory path3 = new address[](2);
        if (_token0 == out_token){
            path3[0] = _token1;
            path3[1] = _token0;
            uint[] memory out_amount = UniswapV2Library.getAmountsOut(_uniswapV2FactoryAddress, amount1, path3);
            return out_amount[out_amount.length - 1] + amount0;
        }else{
            path3[0] = _token0;
            path3[1] = _token1;
            uint[] memory out_amount = UniswapV2Library.getAmountsOut(_uniswapV2FactoryAddress, amount0, path3);
            return out_amount[out_amount.length - 1] + amount1;
        }
       
    }
    
}
