    address public taxReceiveAddress;

    bool public isTaxEnabled;
    mapping(address => bool) public nonTaxedAddresses;

    event LogSetIsTaxEnabled(bool _isTaxEnabled);
    event LogSetAddressTax(address _address, bool ignoreTax);
