        _name = "NIX Bridge Token";
        _symbol = "NBT";
        _decimals = 18;
        __Context_init_unchained();
        __Ownable_init_unchained();

        isTaxEnabled = false;

        TAX_FRACTION = 100; // 1/100 = 1%

        _mint(_msgSender(), INITIAL_SUPPLY); // 60k tokens

        nonTaxedAddresses[_msgSender()] = true;
