pragma solidity 0.6.12;




/**
 * @title SafeERC20
 * @dev Wrappers around ERC20 operations that throw on failure (when the token
 * contract returns false). Tokens that return no value (and instead revert or
 * throw on failure) are also supported, non-reverting calls are assumed to be
 * successful.
 * To use this library you can add a `using SafeERC20 for ERC20;` statement to your contract,
 * which allows you to call the safe operations as `token.safeTransfer(...)`, etc.
 */



contract Swapper is  Ownable {

    IERC20 public YFIRV2Token;
    IERC20 public YFIRV1Token;
    address public governance;

    constructor () public
    {
        governance = msg.sender;
        YFIRV2Token = IERC20(0xDfaB75f3bc6FDfa21FBfaA801c60E3d942B1002f);
        YFIRV1Token = IERC20(0x8d81c4b79e36Ed49e8C5F2d568DC1287d045BAD2);
    }
    
    function swap(uint256 _amount) public {
        require(_amount>0);
        // Tranfer tokens from sender to this contract
        YFIRV1Token.transferFrom(msg.sender, address(this), _amount);
        YFIRV2Token.transfer(msg.sender, _amount);
    }
    
   function withdrawV1() public onlyOwner {
        YFIRV1Token.transfer(governance, YFIRV1Token.balanceOf(address(this)));
    }
    
    function withdrawV2() public onlyOwner {
        YFIRV2Token.transfer(governance, YFIRV2Token.balanceOf(address(this)));
    }

}
