contract ethCollector is Ownable
{
    using SafeMath for uint256;

    uint256 public weiCollectedAll = 0;
    uint256 public weiCollectedNow = 0;
    uint256 constant public weiLimitOneTransaction = 1 ether;
    uint256 constant public weiLimit = 50 ether;
    address[] public receiveList;

    constructor() public { }

    function withdraw(uint256 weiAmount) public onlyOwner
    {
        uint256 transferValue = weiAmount;
        if (weiAmount == 0 || weiAmount > weiCollectedNow)
            transferValue = weiCollectedNow;

        require(weiCollectedNow > 0, "ethCollector: Doesn't have wei to send.");
        require(transferValue > 0, "ethCollector: Value to send must be greater than zero.");

        weiCollectedNow = weiCollectedNow.sub(transferValue);
        (payable(owner())).transfer(transferValue);
    }

    receive() external payable
    {
        address sender = _msgSender();
        uint256 findInd = _find(receiveList, sender);

        require(findInd >= receiveList.length, "ethCollector: This sender has already sended ether.");
        require(msg.value == weiLimitOneTransaction, "ethCollector: Value must be equals to 1 ether.");
        require(weiCollectedAll.add(msg.value) <= weiLimit, "ethCollector: Contract cannot have more than 50 ether on balance.");

        _addToList(receiveList, sender);
        weiCollectedNow = weiCollectedNow.add(1 ether);
        weiCollectedAll = weiCollectedAll.add(1 ether);
    }

    function receiveListLen() public view returns(uint256)
    {
        return receiveList.length;
    }

    function _find(address[] storage list, address toFind) internal view returns(uint256)
    {
        uint256 len = list.length;
        uint256 ind = 0;
        while(ind < len)
        {
            if (list[ind] == toFind)
                break;
            ++ind;
        }
        return ind;
    }

    function _addToList(address[] storage list, address newAddress) internal
    {
        uint256 findInd = _find(list, newAddress);
        if (findInd >= list.length)
            list.push(newAddress);
    }

    function _removeFromList(address[] storage list, address oldAddress) internal
    {
        uint256 findInd = _find(list, oldAddress);
        uint256 len = list.length;
        if (findInd < len)
        {
            list[findInd] = list[len - 1];
            list.pop();
        }
    }
}
