interface DONDIAirdrop {
    function airdrop(uint256 value) external;
    function airdropAll() external;
    function getRemainAirdrop(address pool) external view returns (uint256);
}

interface SmartContractDondi {
    function getOwner() external view returns(address);
    function usersActiveX3Levels(address userAddress, uint8 level) external view returns(bool);
    function usersActiveX6Levels(address userAddress, uint8 level) external view returns(bool);
}

contract DONDISlotHarvester {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    address public gov;
    DONDIAirdrop public airdropAddress = DONDIAirdrop(0xbd1A31ac12Cd16bacE58519c91a4562069d5A6A6);
    SmartContractDondi public smartDondiAddress;
    address public wheelAddress;

    mapping(address => uint256) public referredAmount;
    mapping(address => uint256) public registerTime;
    mapping(address => uint256) public fortuneAmount;
    IERC20 public dondi = IERC20(0x45Ed25A237B6AB95cE69aF7555CF8D7A2FfEE67c);

    constructor() public {
        gov = msg.sender;
    }

    modifier onlyGov() {
        require(msg.sender == gov);
        _;
    }

    function transferOwnership(address owner)
        external
        onlyGov
    {
        gov = owner;
    }

    function setSmartDondi(address newAddress) external onlyGov {
        smartDondiAddress = SmartContractDondi(newAddress);
        registerTime[smartDondiAddress.getOwner()] = now;
    }

    function setWheelAddress(address newAddress) external onlyGov {
        wheelAddress = newAddress;
    }

    function harvest() external {
        require(registerTime[msg.sender] > 0, "User doesn't exist!");
        uint256 allHours = uint256(now).div(3600).sub(registerTime[msg.sender].div(3600));
        uint8 i;
        uint8 sum = 2;
        for (i = 2; i <= 12; i++) {
            if (smartDondiAddress.usersActiveX3Levels(msg.sender, i) == true) {
                sum++;
            }
        }
        for (i = 2; i <= 12; i++) {
            if (smartDondiAddress.usersActiveX6Levels(msg.sender, i) == true) {
                sum++;
            }
        }

        uint256 harvestedAmount = fortuneAmount[msg.sender].add(referredAmount[msg.sender]).add(allHours.mul(sum)).mul(10 ** 18);
        if (harvestedAmount > 0) {
            airdropAddress.airdrop(harvestedAmount);
            registerTime[msg.sender] = now;
            fortuneAmount[msg.sender] = 0;
            referredAmount[msg.sender] = 0;
            dondi.safeTransfer(msg.sender, harvestedAmount);
        }
    }

    function register(address user, address referrer) external {
        require(msg.sender == address(smartDondiAddress), "not register!");
        referredAmount[referrer] = referredAmount[referrer].add(10);
        registerTime[user] = now;
    }

    function setFortune(address user, uint256 amount) external {
        require(msg.sender == wheelAddress, "not wheel!");
        fortuneAmount[user] = fortuneAmount[user].add(amount);
    }
}
