interface RMU {
    function safeTransferFrom(address _from, address _to, uint256 _id, uint256 _amount, bytes calldata _data) external;
    function safeBatchTransferFrom(address _from, address _to, uint256[] calldata _ids, uint256[] calldata _amounts, bytes calldata _data) external;
    function setApprovalForAll(address _operator, bool _approved) external;
    function isApprovedForAll(address _owner, address _operator) external view returns (bool isOperator);
    function balanceOf(address _owner, uint256 _id) external view returns (uint256);
    function mint(address _to, uint256 _id, uint256 _quantity, bytes memory _data) external;
    function endMinting(uint256 _id) external;
    function create(uint256 _maxSupply, uint256 _initialSupply, string calldata _uri, bytes calldata _data) external;
}

contract RMUUtil is Ownable {
    using SafeMath for uint256;

    RMU public ropeMaker;

    constructor(RMU _ropeMakerAddr) public {
        ropeMaker = _ropeMakerAddr;
    }

    // Utility function used to mint in advance some cards reserved for airdrops (Only callable by owner which is the multisig)
    function massMint(uint256[] memory _cardIds, uint256[] memory _amounts) public onlyOwner {
        require(_cardIds.length == _amounts.length, "_cardIds and _amounts have different length");

        uint256 length = _cardIds.length;
        for (uint256 i = 0; i < length; ++i) {
            ropeMaker.mint(msg.sender, _cardIds[i], _amounts[i], "");
        }
    }

    // Utility function used to end minting of multiple cards in one tx (Only callable by owner which is multisig)
    function massEndMinting(uint256[] memory _cardIds) public onlyOwner {
        uint256 length = _cardIds.length;
        for (uint256 i = 0; i < length; ++i) {
            ropeMaker.endMinting(_cardIds[i]);
        }
    }

    // Utility function used to create multiple cards in one tx (Only callable by owner which is multisig)
    function massCreate(uint256[] memory _maxSupply) public onlyOwner {
        uint256 length = _maxSupply.length;
        for (uint256 i = 0; i < length; ++i) {
            ropeMaker.create(_maxSupply[i], 0, "", "");
        }
    }

    // Utility function to send multiple cards to different addresses with different amounts (Callable by anyone, but need approval on this contract)
    function multiTransfer(address[] memory _addresses, uint256[] memory _cardIds, uint256[] memory _amounts) public {
        require(_cardIds.length == _amounts.length && _addresses.length == _cardIds.length, "All input array must have same length");
        uint256 length = _cardIds.length;
        for (uint256 i = 0; i < length; ++i) {
            ropeMaker.safeTransferFrom(msg.sender, _addresses[i], _cardIds[i], _amounts[i], "");
        }
    }

    // To destroy this contract if we want to remove it
    function close() public onlyOwner {
        selfdestruct(msg.sender);
    }
}
