contract Token is ERC20, Ownable, ERC20Detailed {
    
    using SafeMath for uint256;

    address public UNIROUTER    = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    address public FACTORY      = 0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f;
    address public WETHAddress  = Uniswap(UNIROUTER).WETH();
    bool    public priceCapped;
    address public tigerAddress = address(this);
    address public pairAddress;
    address public fundAddress; // address where all the pump money goes
    uint256 internal DECIMAL = 10**18;

    constructor () public ERC20Detailed("TigerCoin", "TIGER", 18) {
        _mint(msg.sender, 50 * (10 ** uint256(decimals())));
        address pairaddy = genUniAddr(tigerAddress, WETHAddress);
        setPairAddress(pairaddy);
        _approve(tigerAddress, UNIROUTER, 1000000000000000000000000);
    }
    
    
    function sqrt(uint y) public pure returns (uint z) {
        if (y > 3) {
            z = y;
            uint x = y / 2 + 1;
            while (x < z) {
                z = x;
                x = (y / x + x) / 2;
            }
        } else if (y != 0) {
            z = 1;
        }
    }
    
    function price() public view returns (uint){
        
        address poolAddress = Uniswap(FACTORY).getPair(tigerAddress, WETHAddress);
        
        uint ethAmount = IERC20(WETHAddress).balanceOf(poolAddress); //Eth in uniswap
        uint tokenAmount = IERC20(tigerAddress).balanceOf(poolAddress); //token in uniswap
        if (tokenAmount == 0) {
            return 0;
        }
        return (DECIMAL.mul(ethAmount)).div(tokenAmount);
    }

    function capPrice(bool input) public onlyOwner {
        priceCapped = input;
    }

    function setPairAddress(address input) public onlyOwner {
        pairAddress = input;
    }
    
    function setFundAddress(address input) public onlyOwner {
        fundAddress = input;
    }
    
    function genUniAddr(address left, address right) public view returns (address) {
        address first = left < right ? left : right;
        address second = left < right ? right : left;
        address pair = address(uint(keccak256(abi.encodePacked(
          hex'ff',
          FACTORY,
          keccak256(abi.encodePacked(first, second)),
          hex'96e8ac4277198ff8b6f785478aa9a39f403cb768dd02cbee326c3e7da348845f'
        ))));
        return pair;
    }
    
    function peg() public {
        if(priceCapped && price() >= (1.05 * 10**18)){
            uint t = IERC20(tigerAddress).balanceOf(pairAddress); //token in uniswap
            uint a = IERC20(WETHAddress).balanceOf(pairAddress); //Eth in uniswap
            uint x = (sqrt(9*t*t + 3988000*a*t) - 1997*t)/1994;
            
            _mint(tigerAddress, x);
            
            address[] memory path = new address[](2);
            path[0] = tigerAddress;
            path[1] = WETHAddress;
            Uniswap(UNIROUTER).swapExactTokensForETH(x, 1, path, fundAddress, 10000000000);
        }
    }
}
