pragma solidity ^0.5.16;







// Inheritance





contract StakingRewards is
    IStakingRewards,
    RewardsDistributionRecipient,
    ReentrancyGuard,
    Pausable,
    ADDRESSBOOK
{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    /* ========== STATE VARIABLES ========== */

    IERC20 public rewardsToken;
    IERC20 public stakingToken;
    uint256 public periodFinish = 0;
    uint256 public rewardRate = 0;
    uint256 public rewardsDuration = 1 days;
    uint256 public lastUpdateTime;
    uint256 public rewardPerTokenStored;
    address public vault;
    address public zap;
    uint256 public TAX = 500;

    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public rewards;
    mapping(address => uint256) public lastUpdateRound;
    mapping(address => uint256) public withdrawAllowanceStored;

    uint256 private _totalSupply;
    mapping(address => uint256) private _balances;

    /* ========== CONSTRUCTOR ========== */

    constructor(
        address _owner,
        address _stakingToken
    ) public Owned(_owner) {
        rewardsToken = IERC20(TOKEN_ADDRESS_711);
        stakingToken = IERC20(_stakingToken);
        rewardsDistribution = IRewardsDistributor(REWARDS_DISTRIBUTOR);
        vault = VAULT;
        setZapper(ZAP);
    }

    /* ========== VIEWS ========== */

    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) external view returns (uint256) {
        return _balances[account];
    }

    function lastTimeRewardApplicable() public view returns (uint256) {
        return Math.min(block.timestamp, periodFinish);
    }

    function rewardPerToken() public view returns (uint256) {
        if (_totalSupply == 0) {
            return rewardPerTokenStored;
        }
        return
            rewardPerTokenStored.add(
                lastTimeRewardApplicable()
                    .sub(lastUpdateTime)
                    .mul(rewardRate)
                    .mul(1e18)
                    .div(_totalSupply)
            );
    }

    function earned(address account) public view returns (uint256) {
        return
            _balances[account]
                .mul(rewardPerToken().sub(userRewardPerTokenPaid[account]))
                .div(1e18)
                .add(rewards[account]);
    }

    function getRewardForDuration() external view returns (uint256) {
        return rewardRate.mul(rewardsDuration);
    }

    function withdrawAllowed(address account) public view returns (uint256) {
        return 
            _balances[account]
                .mul(rewardsDistribution.currentRound().sub(lastUpdateRound[account]))
                .mul(1e16)
                .div(1e18)
                .add(withdrawAllowanceStored[account]);
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    function stake(uint256 amount)
        external
        nonReentrant
        notPaused
        updateReward(msg.sender)
        checkIfNotifiable
    {
        require(amount > 0, "Cannot stake 0");
        uint256 tax = amount.mul(TAX).div(10000);
        amount = amount.sub(tax);
        stakingToken.safeTransferFrom(msg.sender, vault, tax);
        _totalSupply = _totalSupply.add(amount);
        _balances[msg.sender] = _balances[msg.sender].add(amount);
        stakingToken.safeTransferFrom(msg.sender, address(this), amount);
        emit Staked(msg.sender, amount);
    }

    function stakeFor(address to, uint256 amount)
        external
        nonReentrant
        notPaused
        updateReward(to)
        checkIfNotifiable
    {
        require(msg.sender == zap, "Only from ZapIn contract");
        require(amount > 0, "Cannot stake 0");
        _totalSupply = _totalSupply.add(amount);
        _balances[to] = _balances[to].add(amount);
        stakingToken.safeTransferFrom(msg.sender, address(this), amount);
        emit Staked(to, amount);
    }

    function withdraw()
        public
        nonReentrant
        updateReward(msg.sender)
        checkIfNotifiable
    {
        uint256 balance = _balances[msg.sender];
        require(balance > 0, "Cannot withdraw 0");
        uint256 amountToUser = withdrawAllowanceStored[msg.sender];
        amountToUser = amountToUser > balance ? balance : amountToUser;
        uint256 amountToVault = balance.sub(amountToUser);
        _totalSupply = _totalSupply.sub(balance);
        _balances[msg.sender] = 0;
        withdrawAllowanceStored[msg.sender] = 0;
        stakingToken.safeTransfer(vault, amountToVault);
        emit WithdrawnToVault(vault, amountToVault);
        stakingToken.safeTransfer(msg.sender, amountToUser);
        emit WithdrawnToUser(msg.sender, amountToUser);
    }

    function getReward() public nonReentrant updateReward(msg.sender) checkIfNotifiable {
        uint256 reward = rewards[msg.sender];
        if (reward > 0) {
            rewards[msg.sender] = 0;
            rewardsToken.safeTransfer(msg.sender, reward);
            emit RewardPaid(msg.sender, reward);
        }
    }

    function exit() external {
        withdraw();
        getReward();
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    function notifyRewardAmount(uint256 reward)
        external
        onlyRewardsDistribution
        updateReward(address(0))
    {
        if (block.timestamp >= periodFinish) {
            rewardRate = reward.div(rewardsDuration);
        } else {
            uint256 remaining = periodFinish.sub(block.timestamp);
            uint256 leftover = remaining.mul(rewardRate);
            rewardRate = reward.add(leftover).div(rewardsDuration);
        }

        // Ensure the provided reward amount is not more than the balance in the contract.
        // This keeps the reward rate in the right range, preventing overflows due to
        // very high values of rewardRate in the earned and rewardsPerToken functions;
        // Reward + leftover must be less than 2^256 / 10^18 to avoid overflow.
        uint256 balance = rewardsToken.balanceOf(address(this));
        require(
            rewardRate <= balance.div(rewardsDuration),
            "Provided reward too high"
        );

        lastUpdateTime = block.timestamp;
        periodFinish = block.timestamp.add(rewardsDuration);
        emit RewardAdded(reward);
    }

    // Added to support recovering LP Rewards from other systems to be distributed to holders
    function recoverERC20(address tokenAddress, uint256 tokenAmount)
        external
        onlyOwner
    {
        require(
            tokenAddress != address(stakingToken) &&
                tokenAddress != address(rewardsToken),
            "Cannot withdraw the staking or rewards tokens"
        );
        IERC20(tokenAddress).safeTransfer(owner, tokenAmount);
        emit Recovered(tokenAddress, tokenAmount);
    }

    function setZapper(address _zapper) public onlyOwner {
        zap = _zapper;
    }

    /* ========== MODIFIERS ========== */

    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = lastTimeRewardApplicable();
        if (account != address(0)) {
            rewards[account] = earned(account);
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
            withdrawAllowanceStored[account] = withdrawAllowed(account);
            lastUpdateRound[account] = rewardsDistribution.currentRound();
        }
        _;
    }

    modifier checkIfNotifiable() {
        _;
        if(rewardsDistribution.isNotifiable()) {
            rewardsDistribution.distribute(false);
        }
    }

    /* ========== EVENTS ========== */

    event RewardAdded(uint256 reward);
    event Staked(address indexed user, uint256 amount);
    event WithdrawnToUser(address indexed user, uint256 amount);
    event WithdrawnToVault(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);
    event Recovered(address token, uint256 amount);
}
