contract CrowSale is Pausable,WhitelistAdminRole{
    
    
    using SafeMath for uint256;
    uint256  public rate=100;
    uint256 public maxSale=500000000000000000000000;
    uint256 public alreadySale=0;
    
    ERC20Mintable erc20 = ERC20Mintable(0x5284d793542815354b9604f06Df14f157BE90462);
    mapping(address=>uint256) alreadyBuy;
    
	function () payable external
	{
	    crowSale();
	}
	function crowSaleExt() payable external
	{
	    crowSale();
	}
	
	function crowSale() internal whenNotPaused{
	    uint256 mintTokeAmount=rate.mul(msg.value);
	    require(alreadySale.add(mintTokeAmount)<=maxSale,"out of max sale!");
	    require(alreadyBuy[msg.sender].add(mintTokeAmount)<=20 ether,"buy too much");
	    erc20.mint(msg.sender,mintTokeAmount);
	    alreadySale=alreadySale.add(mintTokeAmount);
	    alreadyBuy[msg.sender]=alreadyBuy[msg.sender].add(mintTokeAmount);
	}
	function etherProceeds(uint256 amount) external
		onlyWhitelistAdmin
	{
		if(!msg.sender.send(amount)) revert();
	}   
	function refreshRate(uint256 _rate) external
		onlyWhitelistAdmin
	{
	    rate=_rate;
	}
	
	function refreshTokenAddr(address _addr) external
		onlyWhitelistAdmin
	{
	    erc20 = ERC20Mintable(_addr);
	}	
   
}
