pragma solidity 0.6.12;

interface dRewards {
    function withdraw(uint256) external;
    function getReward() external;
    function stake(uint256) external;
    function balanceOf(address) external view returns (uint256);
    function exit() external;
    function earned(address) external view returns (uint256);
}

interface dERC20 {
    function mint(address, uint256) external;
    function redeem(address, uint256) external;
    function getTokenBalance(address) external view returns (uint256);
    function getExchangeRate() external view returns (uint256);
}

// File: contracts/vaults/dforce/DForceStrategyBase.sol

pragma solidity 0.6.12;







contract DForceStrategyBase is VaultBase{
    using SafeERC20 for IERC20;
    using Address for address;
    using SafeMath for uint256;

    address public dtoken;

    function _deposit(uint256 _amount) internal {
        if (_amount > 0) {
            IERC20(token).safeApprove(dtoken, 0);
            IERC20(token).safeApprove(dtoken, _amount);
            dERC20(dtoken).mint(address(this), _amount);
        }

        uint256 _d = IERC20(dtoken).balanceOf(address(this));
        if (_d > 0) {
            IERC20(dtoken).safeApprove(lend, 0);
            IERC20(dtoken).safeApprove(lend, _d);
            dRewards(lend).stake(_d);
        }
    }

    function _withdraw(uint256 _amount) internal returns (uint256) {
        uint256 _d = _amount.mul(1e18).div(dERC20(dtoken).getExchangeRate());
        uint256 _before = IERC20(dtoken).balanceOf(address(this));
        dRewards(lend).withdraw(_d);
        uint256 _after = IERC20(dtoken).balanceOf(address(this));
        uint256 _withdrew = _after.sub(_before);
        _before = IERC20(token).balanceOf(address(this));
        dERC20(dtoken).redeem(address(this), _withdrew);
        _after = IERC20(token).balanceOf(address(this));
        _withdrew = _after.sub(_before);
        return _withdrew;
    }

    function _withdrawAll() internal { 
        if(dRewards(lend).balanceOf(address(this)) == 0) return;
        dRewards(lend).exit();
        uint256 _d = IERC20(dtoken).balanceOf(address(this));
        if (_d > 0) {
            dERC20(dtoken).redeem(address(this),_d);
        }
    }

     // Harvest 
    function _harvest() internal {
        dRewards(lend).getReward();
    }

    function _earned() view internal returns(uint256){
        return dRewards(lend).earned(address(this));
    }
}

