pragma solidity 0.6.12;





contract DForceVaultProvider is VaultProvider, DForceStrategyBase {
    
    event Withdrew(uint256 amount,uint256 realAmount);

    constructor(address _token, address _dtoken, address _profit, address _lend, address _controller) public{
        token = _token;
        dtoken = _dtoken;
        profit = _profit;
        lend = _lend;
        controller = _controller;
    }

    function deposit(uint256 _amount) override onlyController external {
        IERC20(token).safeTransferFrom(controller, address(this), _amount);
        _deposit(_amount);
    }

    function withdraw(uint256 _amount) override onlyController external {
        uint256 amount = _amount.mul(101).div(100);
        uint256 realAmount = _withdraw(amount);
        emit Withdrew(amount, realAmount);
        IERC20(token).safeTransfer(controller, _amount);
    }

    function depositAll() override onlyController external {
        uint256 amount = IERC20(token).balanceOf(msg.sender);
        if (amount > 0)
        {
            IERC20(token).safeTransferFrom(controller, address(this), amount);
            _deposit(amount);
        }
    }

    function withdrawAll() override onlyController external returns (uint256 amount){
        _withdrawAll();
        amount = IERC20(token).balanceOf(address(this));
        if (amount > 0)
            IERC20(token).safeTransfer(controller, amount);
    }

    function harvest() override onlyController external returns (uint256 amount){
        _harvest();
        amount = IERC20(profit).balanceOf(address(this));
        if (amount > 0)
            IERC20(profit).safeTransfer(controller, amount);
    }

    function earned() override view external returns(uint256){
        return _earned();
    }

    receive () external payable{}
}
