contract Initializable {
    bool public initialized;

    bool private initializing;

    modifier initializer() {
        require(initializing || !initialized, "already-initialized");

        bool isTopLevelCall = !initializing;
        if (isTopLevelCall) {
            initializing = true;
            initialized = true;
        }

        _;

        if (isTopLevelCall) {
            initializing = false;
        }
    }
}

