// File: contracts/HodlV1.sol

// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;






contract HodlV1 is IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    event StartGame();
    event EndGame();
    event ChangeStrongHandPercentile(uint256 oldPercentile, uint256 newPercentile);
    event ChangeCoolDownTime(uint256 oldCoolDownTime, uint256 newCoolDownTime);

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    string private _name;
    string private _symbol;
    uint8 private immutable _decimals;
    uint256 private _totalSupply;
    uint256 public _burnedSupply;
    uint256 public _strongHandPercentile;
    uint256 public _lastUpdated;
    uint256 public _coolDownTime;
    bool public _gameStarted;

    constructor() public {
        _name = "HODL";
        _symbol = "HODL";
        _decimals = 18;
        _totalSupply = 30000e18; // 30000 * 1e18
        _balances[msg.sender] = _totalSupply;
        _gameStarted = false;
        _strongHandPercentile = 30;
        _coolDownTime = 1 days;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public override view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public override view returns (uint256) {
        return (_balances[account].mul(_totalSupply)).div(_totalSupply.sub(_burnedSupply));
        // return (_balances[account] * _totalSupply) / (_totalSupply - _burnedSupply);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public override view returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            msg.sender,
            _allowances[sender][msg.sender].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amountt) internal {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        uint256 amount = uint256(amountt.mul(_totalSupply.sub(_burnedSupply)).div(_totalSupply));
        //uint256 amount = uint256((amountt * (_totalSupply - _burnedSupply)) / _totalSupply);
        uint256 burn_amount;
        _gameStarted ? burn_amount = amount.div(100) : burn_amount = 0;

        _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(amount.sub(burn_amount));

        _burn(burn_amount);
        emit Transfer(sender, recipient, amount);
    }

    function _burn(uint256 amount) internal {
        _burnedSupply = _burnedSupply.add(amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    modifier onlyStrongHands() {
        require(msg.sender == owner() || balanceOf(msg.sender) > _totalSupply.mul(_strongHandPercentile).div(100), "Caller does not have strong hands");
        _;
    }

    modifier cooledDown() {
        require(now > (_lastUpdated+_coolDownTime));
        _;
    }

    function setStrongHandPercentile(uint256 percentile) public onlyOwner {
        require(percentile <= 55 && percentile >= 5, "Owner cannot rig the game");
        uint256 old = _strongHandPercentile;
        _strongHandPercentile = percentile;
        emit ChangeStrongHandPercentile(old, percentile);
    }

    function setCoolDownTime(uint256 coolDownTime) public onlyOwner {
        uint256 old = _coolDownTime;
        _coolDownTime = coolDownTime;
        emit ChangeCoolDownTime(old, coolDownTime);
    }

    function startGame() external onlyStrongHands cooledDown {
        require(!_gameStarted, "The game has already started");
        _gameStarted = true;
        _lastUpdated = now;
        emit StartGame();
    }

    function endGame() external onlyStrongHands cooledDown {
        require(_gameStarted, "The game has already ended");
        _gameStarted = false;
        _lastUpdated = now;
        emit EndGame();
    }
}
