interface IKoreVault {
    function addPendingRewards(uint _amount) external;
        function depositFor(address depositFor, uint256 _pid, uint256 _amount) external;
}

interface WKORE {
    function deposit(uint) external;
    function withdraw(uint) external;
}

contract KOREv2Router is OwnableUpgradeSafe {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    mapping(address => uint256) public hardKORE;

    address public _koreToken = address(0xA866F0198208Eb07c83081d5136BE7f775c2399e);
    address public _wKoreToken = address(0x0Ab5B06cc83Db3638D296735E498087d598B296B);
    address public _koreWETHPair = address(0xef035F52e5a8974adBF4380e020cD4A0A349C517);
    IFeeApprover public _feeApprover = IFeeApprover(0x891059D5Ed0c5f4838b266bEB7AA7522e73aeb00);
    IKoreVault public _koreVault = IKoreVault(0xE5BeB064d95c440B067Acab01eA4f752B32F63a2);
    IWETH public _WETH = IWETH(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);

    function initialize() public initializer {
        OwnableUpgradeSafe.__Ownable_init();
        refreshApproval();
    }

    function refreshApproval() public {
        IUniswapV2Pair(_koreWETHPair).approve(address(_koreVault), uint(-1));
        IERC20(_koreToken).approve(_wKoreToken, uint(-1));
        IERC20(_wKoreToken).approve(address(_koreVault), uint(-1));
    }

    fallback() external payable {
        if(msg.sender != address(_WETH)){
             addLiquidityETHOnly(msg.sender, false);
        }
    }

    function wrapKOREAndStakeInVault(uint _amount, bool _autoStake) public {
        IERC20(_koreToken).safeTransferFrom(msg.sender, address(this), _amount);

        WKORE(_wKoreToken).deposit(_amount);

        if (_autoStake) {
            _koreVault.depositFor(msg.sender, 1, _amount);
        } else {
            IERC20(_wKoreToken).safeTransfer(msg.sender, _amount);
        }
    }

    function unwrapWKORE(uint _amount) public {
        IERC20(_wKoreToken).safeTransferFrom(msg.sender, address(this), _amount);

        WKORE(_wKoreToken).withdraw(_amount);

        IERC20(_koreToken).safeTransfer(msg.sender, _amount);
    }

    function addLiquidityETHOnly(address payable to, bool autoStake) public payable {
        require(to != address(0), "Invalid address");
        hardKORE[msg.sender] = hardKORE[msg.sender].add(msg.value);

        uint256 buyAmount = msg.value.div(2);
        require(buyAmount > 0, "Insufficient ETH amount");
        _WETH.deposit{value : msg.value}();

        (uint256 reserveWeth, uint256 reserveKore) = getPairReserves();
        uint256 outKore = UniswapV2Library.getAmountOut(buyAmount, reserveWeth, reserveKore);

        _WETH.transfer(_koreWETHPair, buyAmount);

        (address token0, address token1) = UniswapV2Library.sortTokens(address(_WETH), _koreToken);
        IUniswapV2Pair(_koreWETHPair).swap(_koreToken == token0 ? outKore : 0, _koreToken == token1 ? outKore : 0, address(this), "");

        _addLiquidity(outKore, buyAmount, to, autoStake);

        _feeApprover.sync();
    }

    function _addLiquidity(uint256 koreAmount, uint256 wethAmount, address payable to, bool autoStake) internal {
        (uint256 wethReserve, uint256 koreReserve) = getPairReserves();

        uint256 optimalKoreAmount = UniswapV2Library.quote(wethAmount, wethReserve, koreReserve);

        uint256 optimalWETHAmount;
        if (optimalKoreAmount > koreAmount) {
            optimalWETHAmount = UniswapV2Library.quote(koreAmount, koreReserve, wethReserve);
            optimalKoreAmount = koreAmount;
        }
        else
            optimalWETHAmount = wethAmount;

        assert(_WETH.transfer(_koreWETHPair, optimalWETHAmount));
        assert(IERC20(_koreToken).transfer(_koreWETHPair, optimalKoreAmount));

        if (autoStake) {
            IUniswapV2Pair(_koreWETHPair).mint(address(this));
            _koreVault.depositFor(to, 0, IUniswapV2Pair(_koreWETHPair).balanceOf(address(this)));
        }
        else
            IUniswapV2Pair(_koreWETHPair).mint(to);


        //refund dust
        if (koreAmount > optimalKoreAmount)
            IERC20(_koreToken).transfer(to, koreAmount.sub(optimalKoreAmount));

        if (wethAmount > optimalWETHAmount) {
            uint256 withdrawAmount = wethAmount.sub(optimalWETHAmount);
            _WETH.withdraw(withdrawAmount);
            to.transfer(withdrawAmount);
        }
    }

    function changeFeeApprover(address feeApprover) external onlyOwner {
        address oldAddress = address(_feeApprover);
        _feeApprover = IFeeApprover(feeApprover);
    }

    function changeKVault(address koreVault) external onlyOwner {
        address oldAddress = address(_koreVault);
        _koreVault = IKoreVault(koreVault);
    }

    function getLPTokenPerEthUnit(uint ethAmt) public view  returns (uint liquidity){
        (uint256 reserveWeth, uint256 reserveKore) = getPairReserves();
        uint256 outKore = UniswapV2Library.getAmountOut(ethAmt.div(2), reserveWeth, reserveKore);
        uint _totalSupply =  IUniswapV2Pair(_koreWETHPair).totalSupply();

        (address token0, ) = UniswapV2Library.sortTokens(address(_WETH), _koreToken);
        (uint256 amount0, uint256 amount1) = token0 == _koreToken ? (outKore, ethAmt.div(2)) : (ethAmt.div(2), outKore);
        (uint256 _reserve0, uint256 _reserve1) = token0 == _koreToken ? (reserveKore, reserveWeth) : (reserveWeth, reserveKore);
        liquidity = Math.min(amount0.mul(_totalSupply) / _reserve0, amount1.mul(_totalSupply) / _reserve1);
    }

    function getPairReserves() internal view returns (uint256 wethReserves, uint256 koreReserves) {
        (address token0,) = UniswapV2Library.sortTokens(address(_WETH), _koreToken);
        (uint256 reserve0, uint reserve1,) = IUniswapV2Pair(_koreWETHPair).getReserves();
        (wethReserves, koreReserves) = token0 == _koreToken ? (reserve1, reserve0) : (reserve0, reserve1);
    }
}
