pragma solidity =0.6.6;







contract SwapToPrice {
    using SafeMath for uint256;
    address public immutable factory;

    constructor(address factory_) public {
        factory = factory_;
    }
    // computes the direction and magnitude of the profit-maximizing trade
    function computeProfitMaximizingTrade(
        address factory_,
        uint256 truePriceTokenA,
        uint256 truePriceTokenB,
        address tokenA,
        address tokenB
    ) view public returns (bool aToB, uint256 amountIn, uint256 reserveA, uint256 reserveB) {
        // true price is expressed as a ratio, so both values must be non-zero
        require(truePriceTokenA != 0 && truePriceTokenB != 0, "SwapToPrice: ZERO_PRICE");

        (reserveA, reserveB) = UniswapV2Library.getReserves(factory_, tokenA, tokenB);

        aToB = reserveA.mul(truePriceTokenB) / reserveB < truePriceTokenA;

        uint256 invariant = reserveA.mul(reserveB);

        uint256 leftSide = Babylonian.sqrt(
            invariant.mul(aToB ? truePriceTokenA : truePriceTokenB).mul(1000) /
            uint256(aToB ? truePriceTokenB : truePriceTokenA).mul(995)
        );
        uint256 rightSide = (aToB ? reserveA.mul(1000) : reserveB.mul(1000)) / 995;

        // compute the amount that must be sent to move the price to the profit-maximizing price
        amountIn = leftSide.sub(rightSide);
    }
}
