contract fecoreLGE is Ownable {
    using SafeMath for uint256;

    address public FECORE_ADDRESS = address(0);
    IERC20 public FECORE;
    IERC20 public YFFS;

    uint256 public startTime = 1604340000;
    uint256 public endTime;

    modifier validDeposit() {
        require(block.timestamp <= endTime, "End time.");
        require(block.timestamp >= startTime, "Wait to start.");
        _;
    }

    constructor(address _yffs, uint256 _endTime) public {
        YFFS = IERC20(_yffs);
        endTime = _endTime;
    }

    mapping(address => uint256) public _accountBalances;
    address[] public depositors;

    function updateFecoreAddress(address _fecore) external onlyOwner {
        FECORE_ADDRESS = _fecore;
        FECORE = IERC20(FECORE_ADDRESS);
    }

    function addFecoreLGE(uint256 amount) external onlyOwner {
        require(amount > 0);
        require(FECORE_ADDRESS != address(0));
        FECORE.transferFrom(_msgSender(), address(this), amount);
    }

    function YFFSforLP(uint256 amount) external validDeposit {
        require(amount > 0);
        if (_accountBalances[_msgSender()] == 0) {
            depositors.push(_msgSender());
        }

        YFFS.transferFrom(_msgSender(), address(this), amount);

        _accountBalances[_msgSender()] = _accountBalances[_msgSender()].add(
            amount
        );
    }

    function safeStopAfterLGEFinish() external onlyOwner {
        require(block.timestamp > endTime, "End time.");
        YFFS.transfer(owner(), YFFS.balanceOf(address(this)));
        FECORE.transfer(owner(), FECORE.balanceOf(address(this)));
    }
    
}

